css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
    20090715:
        add post textarea width
*/

@-moz-document domain("mobile01.com") {

.page_layout {
    width: 90% !important;
}

.forum_content {
    width: 80% !important;
}

.forum_sidemenu {
    width: 18% !important;
}

.post_detail {
    width: 100% !important;
}

.post_content {
    width: 95% !important;
}

.page_link_t {
    height: auto !important;
    line-height: 1em !important;
}

.page_nav {
    width: 70% !important;
}

.block_usermenu {
    height: auto !important;
}

.text_ad_area {
    display: none !important;
}

.header_bg {
    width: 90% !important;
}

div[style="margin-top: 4px; margin-bottom: 4px;"] {
    display: none !important;
}

.double_ad_l, .double_ad_r {
    display: none !important;
}

.index_ad_block {
    display: none !important;
}

.forum_ad {
    display: none !important;
}

div[style="margin-bottom: 4px;"] {
    display: none !important;
}

.ad_right {
    display: none !important;
}

/* bottom nav bar */
.page_link_b {
    height: auto !important;
}

/* post sig */
.post_sig {
    height: auto !important;
}

/* create new post */
div.help_block {
    width: auto !important;
}

/* write post */
textarea.inpStyle {
    width: 40em !important;
}


}
