css:
@-moz-document domain('reddit-stream.com')
  {
    body {
    background-color: #111;
    color: #aaa;
}
a {
    color: orangered;
}
p {
    line-height: 24px;
}
h1.title {
    color: #ddd;
}
#sidebar {
    background-color: #000;
}
#post-title a {
    color: #eee;
}
.tabbed .tab-bar > span.active {
    background-color: #111;
}
.tabbed .tab-bar > span {
    background-color: #222;
}
#info-tabs .tab-content {
    background-color: #111;
}
#post-info table th {
    background-color: #000;
}
div.c-body {
    background-color: #222;
}
.c-replies .comment, .c-new {
    border-left: 1px dashed #888;
}
.c-time {
    background-color: black !important;
    border: none !important;
    color: gray;
}
div#c-main {
    width: 95%;
}
a.c-username {
    color: lightgray!important;
}
.actions a {
    color: steelblue;
    font-weight: normal;
}
span.c-flair {
    color: orangered!important;
    border: none;
    font-style: italic;
}
div.c-body {
    border: none;
}
a.c-points {
    color: darkgray!important;
}
.c-header a.has-replies {
    background-color: black;
}
  }
