css:
@-moz-document domain("iflscience.com") {
:root {
    --background-color-dark: #111;
    --background-color-medium: #222;    
    --background-color-light: #444;
    --text-color-medium: #ccc;
    --text-color-light: #eee;
    --border-color-dark: #444;
    --border-color-light: #888;
    
    --spotim-card-background-color: var(--background-color-dark);
    --spotim-text-color-muted: var(--text-color-light);
    --spotim-pitc-top-comment-color: var(--text-color-medium);
}

}

@-moz-document domain("iflscience.com") {
body{
    background-color: var(--background-color-dark);
    color: var(--text-color-medium);
}
.header, .header.smaller{
    background-color: var(--background-color-light);
}
article .content, article.single .top{
    background-color: var(--background-color-medium);
}
article .content .title a{
    color: var(--text-color-medium);
}
.sidebar .widget{
    background-color: var(--background-color-medium);
    border: 1px solid var(--border-color-light);
}
.sidebar .widget .post-list.post-list.alt li{
    outline: 1px solid var(--border-color-dark);
}
.sidebar .widget .post-list li .post-content h4 a{
    color: var(--text-color-medium);
}

.category-title span, .section-title span{
    background-color: var(--background-color-medium)
}
}
