@license      CC-BY-SA-4.0
@author       Michael Jaggers <michael.jaggers@gmail.com> (https://michaeljaggers.com)

==/UserStyle== */

@-moz-document domain("pinboard.in"), url-prefix("https://pinboard.in/") {

body { 
    background-color: #FEFEFE; 
}

a {
  color: #2196f3 !important;
}

#footer {
  visibility: hidden;
}

#banner { 
    padding-bottom: 12px;
    background-color: #FEFEFE;
    border-bottom: 1px solid #C0C0C0 !important;
}

#logo { 
    background: url('data:image/svg+xml;charset=UTF-8,<svg version="1.1" height="30" viewBox="0 0 32 32" width="150" xmlns="http://www.w3.org/2000/svg"><path d="m14.5 9.496-7-5.996v-3l-7 7h2.993l6.007 7v4l9.003-9z" fill="%232196f3"></path><path d="m7.885.057c-.28-.117-.604-.052-.818.163l-6.847 6.847c-.215.215-.279.537-.163.818.116.28.39.463.693.463h2.583l5.472 6.376v3.635c0 .669.81 1.001 1.28.53l3.326-3.326 9.343 8.248c.142.127.319.189.496.189.193 0 .386-.074.531-.221.28-.281.293-.732.028-1.028l-8.286-9.299 3.368-3.368c.215-.214.278-.537.163-.817-.116-.28-.39-.463-.692-.463l-3.64-.003-6.374-5.462v-2.589c0-.303-.183-.577-.463-.693zm6.072 10.063c.136.116.309.18.487.18l2.107.002-6.247 6.246v-2.103c0-.179-.064-.353-.181-.488l-5.876-6.848c-.143-.166-.351-.261-.569-.261h-1.117l4.287-4.287v1.124c0 .219.096.427.262.57z"></path></svg>') no-repeat;
    background-position: top 3px left -58px;
}

#pinboard_name { 
    font-size: 26px; 
    margin-left: 35px; 
    margin-right: 10px;
    vertical-align: sub;
    color: #333 !important;
}

#logo span { 
    color: #C0C0C0; 
    vertical-align: text-bottom; 
}

#logo .banner_username { 
    color: #222 !important;
    font-weight: 900;
    padding: 0 3px; 
    margin-top: -2px
}

.pin_logo { 
    display: none; 
}

#top_menu { 
    margin-top: 10px; 
}


/* User Options */
.user_navbar a {
  color: #666 !important;
  display: inline-block;
  margin-top: 4px;
}

.user_navbar .filter::before {
    content: "/";
}

.user_navbar .filter:hover { color: #222 !important; }


.rss_linkbox { 
    margin-top: 0px;
    width: 45px;
    background: url('data:image/svg+xml;charset=UTF-8,<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24"><path fill="none" d="M0 0h24v24H0V0z"/><circle fill="%23ee9922" cx="6.18" cy="17.82" r="2.18"/><path fill="%23ee9922" d="M4 4.44v2.83c7.03 0 12.73 5.7 12.73 12.73h2.83c0-8.59-6.97-15.56-15.56-15.56zm0 5.66v2.83c3.9 0 7.07 3.17 7.07 7.07h2.83c0-5.47-4.43-9.9-9.9-9.9z"/></svg>') no-repeat;
    background-position: bottom right
}

.user_navbar .rss_link {
    font-weight: 900;
    font-size: 1em !important;
    color: #555 !important;
    width: 45px;
}

#bulk_edit { 
    display: inline-block;
    margin-left: 20px;
}

#bulk_edit:hover { 
    color: #222 !important;
}

#nextprev .edit:hover { color: #222 !important; }

#timer {
  display: none !important;
}

#right_bar {
  margin-left: 0 !important;
}

.search_button {
  margin-top: 10 !important;
}

#search_query_field {
  padding: 8px 7px !important;
  border: 1px solid #bbb !important;
  border-radius: 2px !important;
}

input[type="submit"],
input[type="button"],
input.submit,
input.reset {
  padding: 8px 21px 8px !important;
  border: none !important;
  border-radius: 2px !important;
  background: #333 !important;
  color: #FFF !important;
  font-weight: bold !important;
}

input[type="submit"]:hover {
    cursor: pointer;
}

input[type="button"],
input.submit,
input.reset {
  background: #EEEEEE!important;
  color: #555!important;
}

input,
textarea {
  padding: 7px 8px!important;
  border: 1px solid #ddd;
  border-radius: 3px!important;
  color: #555!important;
  font-weight: bold!important;
}

#edit_bookmark_form input[type="button"]:hover,
input.submit:hover,
input.reset:hover {
  background: #7FC10B!important;
  color: #FFF!important;
}

/* Redundant Account Username */
.small_username b a {
  display: none;
}

/* User Bookmark Count */
#main_column .bookmark_count_box {
    width: 230px;
}

#main_column .bookmark_count_box::after {
    content: "filters";
    float: right;
    padding-top: 3px;
    font-weight: 900;
    color: #333;
    
    
}

#main_column .bookmark_count { 
    font-size: 20px; 
    color: #000;
    font-weight: 800
}

#main_column .bookmark_count::after {
    content: "bookmarks";
    padding-left: 5px;
    color: #555;
    font-size: 16px;
    font-weight: normal;
}

/* Next/Prev Controls */
.next_prev,
#per_page_picker a {
  font-size: 16px!important;
}

#pinboard_name {
  font-weight: 900!important;
}




/* Bookmarks */
.bookmark { 
    padding-top: 15px;
    padding-bottom: 5px;
    border-bottom: 1px solid #DDD !important;
    position: relative;
}

.bookmark .display {
    position: relative;
}

.bookmark_title  {
  color: #222 !important;
  font-weight: bold!important;
  font-size: 20px!important;
}

.bookmark_title.unread,
.bookmark_title.unread:visited {
  color: #FF9113!important;
}

.private {
  background: transparent !important;
  border: none !important;
}

.private .bookmark_title {
  color: #FF2E54 !important;
}

.url_link { 
    background: transparent !important; 
    color: #666 !important; 
    font-style: italic;
}

.url_link:before {
    content: "+"
}

.description { 
    margin-top: 5px;
    line-height: 130%;
    font-size: 15px;
    color: #555;
    margin-bottom: 0;
}

.star {
  float: right !important;
  font-size: 2em;
  position: absolute;
  top: 11px;
  right: -3px;
  z-index: 9;
}

.bookmark:hover .star {
  visibility: visible!important;
  color: #ccc!important;
}

.bookmark:hover .selected_star:hover,
.bookmark:hover .selected_star {
  visibility: visible !important;
  color: #F7B42C !important;
}
.bookmark:hover .star:hover {
  visibility: visible !important;
  color: #F7B42C !important;
}

.star.selected_star {
  visibility: visible !important;
  color: #F7B42C !important;
}

.when,
.edit_links,
.star,
.bookmark .url_link {
  visibility: hidden !important;
}

.bookmark .url_link {
    display: inline-block;
    margin-top: 10px;
}

.bookmark .when {
  display: inline-block;
  font-size: 11px;
  font-weight: 900;
  color: #555 !important;
}

.bookmark:hover .when::before {
    content: "added "
}



.bookmark .edit_links a.edit:hover {
    color: #222 !important;
}

.bookmark .edit_links a.delete:hover {
    color: #ff4242 !important;
}

.bookmark .when,
.bookmark .edit_links a,
.bookmark .url_link {
    padding-top: 3px;
}

.bookmark:hover .when { left: 0; }


.bookmark:hover .when,
.bookmark:hover .edit_links a,
.bookmark:hover .url_link {
  visibility: visible !important;
  
}

.bookmark .tag { 
    display: inline-block;
    margin-top: 10px;
    line-height: 135%;
    margin-top: 30px;
    margin-bottom: -5px;
}




/* Tag Cloud */
.tag,
.per_page_selected {
  padding: 2px 6px;
  background-color: #777 !important;
  color: #FDFDFD !important;
  font-weight: 700 !important;
  border: 1px solid #666;
  border-radius: 2px;
}

.tag_table a {
  display: block!important;
  margin-bottom: 3px!important;
  color: #FF9113!important;
  font-weight: normal!important;
  font-size: 16px!important;
  line-height: 100%!important;
  border-radius: 3px!important;
}

#edit_bookmark_form input {
  line-height: 1.8!important;
  font-size: 110%!important;
  padding-top: 0.35rem!important;
}
  
} /* END @-moz-document */
