css:
@-moz-document url-prefix("https://clockify.me/") {
/*NAVIGATION*/
@media (min-width: 992px){
    .cl-card {
    	border-bottom:none;
	}
    .cl-layout-aside {
    width: 264px;
    border-left: .2857rem solid white;
    }
}
.cl-hover-shadow:hover {
    box-shadow: 0 0 0px rgba(0,0,0,.1);
    background-color: rgb(3 169 244 / .03);
}
.cl-card {
    border: none;
    border-radius: 10px;
    box-shadow: 0 20px 20px -10px rgb(198 210 217 / .8);
}
.cl-card-header {
    background-color: transparent;
}
.cl-h6, h6 {
    color:  rgb(3 169 244);
}
.cl-sidebar-header {
    border-left: none;
}
.cl-navbar-expand-lg .cl-navbar-nav .cl-nav-link {
    border-left: none !important;
}
.cl-sidebar-container .cl-nav-item.cl-active .cl-nav-link, .cl-sidebar-container .cl-nav-link.cl-active {
    border-left: .2857rem solid rgb(3 169 244) !important;
}
.cl-bg-green {
    background: rgb(3 169 244);
}
body {
   background-color: var(--blue-gray-2);
}
}

@-moz-document url("https://clockify.me/tracker") {
/*TIME TRACKER*/
parent-tracker-entry, time-tracker-entry {
    border-bottom: 1px solid rgb(228 234 238 / .3);
}
}
