css:
@-moz-document url-prefix("https://indavideo.hu") {
:root {
	--bg-main: #181818;
	--bg-card: #272828;
	--border: #555;
	--text-dark: #909090;
	--text-light: #fff;
}

#footer,
footer,
body > div.base_container.container > div:nth-child(4) > div.res_rightcontent,
div.res_facebook_box.mx-auto,
body > div.base_container.container > div:nth-child(6),
.vid_comments,
.res_channel_bar.content_box,
.vid_header.content_box,
.navbar_logo,
.navbar_navs > ul > li:nth-child(2),
.navbar_navs > ul > li:nth-child(3),
.navbar_navs > ul > li:nth-child(4),
body > div.indapass-inda-hu,
.vid_undervideo_updown,
.vid_undervideo.content_box > div.d-flex.flex-row.flex-wrap.px-0.pt-2,
.vid_tags_content,
.topAdv {
	display: none !important;
}

body {
	background: var(--bg-main);
	color: var(--text-dark);
	font-size: 1.0rem;
}

/* Navbar */

header {
	margin-top: 15px;
	margin-bottom: 50px;
}

.navbar_main {
	border: none;
}

.navbar_content ul li {
	background: transparent;
}

.navbar_content ul li a {
	color: var(--text-dark);
}

.navbar_content ul li:hover,
.navbar_content ul li a:hover {
	background: var(--bg-card);
}

.form-control:focus {
	box-shadow: 0 0 0 0.15rem rgba(132, 133, 134, .25);
}

#inpt_head_searchbox {
	color:  var(--text-light);
	background: var(--bg-main);
	border: 1px var(--border) solid;
}

#btn_search {
	background: var(--bg-card);
}

#btn_search:active {
	background: var(--bg-card);
	border: none;
	box-shadow: none;
}

.navbar_search_icon:before {
	color: var(--text-dark);
}

.navbar_search_icon:hover:before{
	color: var(--text-light);
}

.navbar_search_icon:focus {
	outline: none;
}

/* Navbar mobile */
.navbar_content_mobile {
	background: var(--bg-card);
	height: 70px;
	padding-top: 15px !important;
	overflow: hidden;
}

.navbar_mobile_dropdown > ul {
	border: none;
}

.navbar_mobile_dropdown > ul > li,
.navbar_mobile_search {
	background: var(--bg-card) !important;
}

.navbar_mobile_dropdown *,
.navbar_mobile_dropdown *:before{
	color: var(--text-dark) !important;
}


/* Video area */

.vid_des_footer {
	border-top: 1px solid var(--border);
}

.base_row,
.res_rightcontent,
.res_leftcontent {
	background: var(--bg-main) !important;
}

.vid_uservideos_title a,
.vid_simvids_title a {
	color: var(--text-light);
}

.vid_uservideos_owner a,
.vid_simvids_owner a {
	color: var(--text-dark);
}

.vid_uservideos_date,
.vid_simvids_date {
	color: var(--text-dark) !important;
}

.vid_undervideo_like:before,
.vid_undervideo_dislike:before,
.vid_undervideo_comnum:before,
.vid_undervideo_loop:before,
.vid_undervideo_plus:before,
.vid_undervideo_clock:before,
.vid_undervideo_visitnum:before,
.vid_undervideo_fb:before,
.vid_undervideo_gplus:before,
.vid_undervideo_twitter:before,
.vid_undervideo_tumblr:before,
.vid_undervideo_reddit:before,
.vid_undervideo_report:before,
.vid_undervideo_embed:before,
.vid_undervideo_message:before,
.vid_undervideo_addtomyplaylist:before,
.add_watch_later:before,
.icon_less:before {
	color: var(--text-dark) !important;
}

.vid_undervideo.content_box,
.content_box * {
	color: var(--text-light);
	background: var(--bg-card);
}

div.base_container.container > div:nth-child(4) > div.res_leftcontent {
	flex: 1;
	max-width: 100%;
	padding-right: 0 !important;
}

.vid_addtomyplaylist_content {
	margin-top: 0px !important;
}

.res_rightcontent > div > div > div {
	padding-top: 0 !important;
}

.vid_uservideos_footer,
.vid_simvids_footer {
	margin-top: 0 !important;
	border: none;
}

.res_rightcontent,
.res_rightcontent > div > div > div {
	padding-bottom: 0 !important;
}

.res_rightcontent {
	margin-bottom: 30px !important;
}

.vid_des_content {
	line-height: 1.5rem;
}

/* Browse page*/


#whitepaper,
.original_browse > .header,
#p_views > div,
#p_rate_num > div,
#p_comment_num > div,
#p_date > div {
	background: var(--bg-card) !important;
}

.item a.title,
#p_views > div strong,
#p_views > div div > div.text,
#p_rate_num  div > strong,
#p_rate_num > div div.text,
#p_comment_num > div strong,
#p_comment_num > div  div > div.text,
#pg_p_date > div > div > strong,
#pg_p_date > div > div > div > div > div.text,
#BOX_0 > div.header.BROWSE.default_header > div > div.tab_center {
	color: var(--text-light);
}

.btn_tab > .tab_center{
	padding-top: 9px !important;
	margin: 0 !important;
}

.btn_tab.selected_tab {
	background: var(--border) !important;
}

.btn_tab.selected_tab * {
	border: none !important
}

.upload_time {
	color: var(--text-dark) !important;
}

#pg_p_views > div > div > a,
#pg_p_rate_num > div > div > a,
#pg_p_comment_num > div > div > a,
#pg_p_date > div > div > a {
	color: var(--text-dark);
}

.item *,
.original_browse > .header{
	border: none !important;
}

}
