css:
@-moz-document domain("www.nitrotype.com") {
.ad {
    display: none;
}
}
@-moz-document url-prefix("https://www.nitrotype.com/race/") {

.ad, .racev3-track, .raceChat-pickerOpt, .raceChat-bubbles, .dash-bg, .nmDash-message, .dashShield-layer--mid, .dashShield-layer--btm, .raceChat-pickerToggle, .dash-pos, .dash-nitro, .dash-side[data-tip^="During"], .race-host-controls--info, .racev3Pre-tip, .dash-inv, .notification--friend-online--info, .notification, .raceResults-flag-bg, .animate--iconSlam::before, .animate--iconSlam svg, .animate--iconSlam::after, .pop--secondary::before, .pop--secondary::after, footer, .footer, .growls, .raceResults-placement-other {
    display: none!important;
}

body {
    background: linear-gradient(130deg, #2E3141 0%, #2E3141 20%, #303343 20%, #303343 25%, #2E3141 25%, #2E3141 27%, #303343 27%, #303343 50%, #2E3141 50%, #2E3141 85%, #303343 85%, #303343 90%, #2E3141 90%);
}
.gridTable-row:not(.is-self), .raceResults-placement-winner, .raceResults-header.split .split-cell:first-child, .hypebar-container, .g-b--9of12, .raceResults-sidebar, .gridTable-row.is-self::before, .gridTable-row.is-self::after, .gridTable-row.is-self .pane, .is-self .gridTable-cell--end, .gridTable-progress, .gridTable > div:first-child > div:nth-child(2) > div:nth-child(2):not(.split-cell):not(.list-item), .structure-spacer-left, body > span, .takeoverTemp {
    display: none!important;
    align-items: unset!important;
}
.gridTable--raceResults .gridTable-row {
    grid-template-columns: unset;
}
.gridTable--raceResults .gridTable-row:first-child {
    margin-bottom: 0px;
}
.raceResults-playerName div div {
    transform: unset!important;
}
.gridTable-row.is-self .gridTable-cell:not(.gridTable-cell--end) {
    align-items: flex-start;
    width: 100%;
}
.gridTable--raceResults .gridTable-row:first-child:not(.is-wampus):hover, .gridTable--raceResults .gridTable-row:first-child:not(.is-wampus) {
    background: unset;
    border: unset;
}
.gridTable-row.is-self, .raceResults-body {
    background: #303343!important;
    border: none;
    box-shadow: none;
}
.gridTable--raceResults {
    height: unset;
}
.raceResults-footer {
    position: absolute;
    background: unset;
    width: 250px;
    left: 62.5%;
    top: 20px;
    border: none;
}
.g-b--3of12 {
    width: unset;
    max-width: unset;
}
.race-results.is-minimized {
    border: none;
    box-shadow: none;
    height: 90px!important;
}
.raceResults-mini {
    border-radius: 0 0 0 20px;
    position: absolute;
    transform-origin: unset;
    left: unset;
    right: 0px;
    transform: translate(0%, 0)!important;
    box-shadow: none;
}

.racev3-ui {
    position: absolute;
    top: 40px;
}
.dashShield {
    position: absolute!important;
    top: unset;
    height: unset;
}
.sound-controls .list {
    border-radius: 0px 0px 0px 0px !important;
}
.raceChat-picker {
    position: static;
    transform: none!important;
}
.raceChat-pickers, .raceChat {
    position: unset;
    box-sizing: unset;
}
.raceChat-pickerOptions {
    position: absolute;

    height: 6px;
    top: 0px;
    left: unset;
    background: linear-gradient(to left, #680e0e 30%, #9c0202 100%);
    border-radius: 0px;
}
dash, .dash-content {
    width: 1000px;
    min-height: 200px;
    height: auto;
}
.dash, .raceChat-pickerOptions {
    width: 97%!important;
}


.racev3-header {
    height: 0px;
}

.dashShield-layer {
    position: unset;
}
.is-single-line .dash-copy
{
    height: 150px!important;
    padding-top: 40px;
}

.dash, .racev3
{
    min-height: 500px;
    height: auto;
    padding-bottom: 90px;
    margin-bottom: 10px;
    background: none;
}

.card--shadow {
    box-shadow: none;
}

.dash-center div[class="dash-copyContainer"] .dash-copy
{
    transform: none!important;
}

.dash-actions
{
    position: absolute;
    top: -20px;
    left: 250px;
}

.dash-copyContainer
{
    margin-top: 15px;
}

.race-results.has-minimized, .raceResults--default {
    height: unset;
}
canvas, img:not(.img--noMax):not([alt='Finish']) {
    display: none!important;
}
.race-results {
    box-shadow: none;
}
}
