css:
@-moz-document domain('stillepost.ca') {

/* v0.1 created by Julie Fournier admin@stillepost.ca, 15 June 2009 11:00 */

	body {
		background-color: #FFF !important;
		color: #333 !important;
		}

	a:link { color: #666 !important; }
	a:hover { color: #999 !important; }
	a:active { color: #CCC !important; }
	a:visited { color: #333 !important; }

	.nav, .nav:link, .nav:visited  { color: #666 !important; }

	img {
		opacity: 0.2 !important;
		-moz-opacity: 0.2 !important;
		}

	#ajax_in_progress {
		background: #999;
		color: #FFF;
		}

	.bordercolor, .tborder,
	.catbg, tr.catbg td,  
	hr, .hrcolor, 
	.maintab_first, .maintab_back, .maintab_last,
	.mirrortab_first, .mirrortab_back, .mirrortab_last, 
	#preview_body, 
	.titlebg, tr.titlebg th, tr.titlebg td, 
	.windowbg {
		background: url(data:image/gif;base64,R0lGODlhAQABAJEAAAAAAP///////wAAACH5BAEAAAIALAAAAAABAAEAAAICVAEAOw==) no-repeat !important;
		background-color: #FFF !important;	
		}

	.catbg2, tr.catbg2 td, 
	.maintab_active, .maintab_active_first, .maintab_active_back, .maintab_active_last, 
	.mirrortab_active, .mirrortab_active_last, 
	.quote, 
	.titlebg2,  tr.titlebg2 th, tr.titlebg2 td, 
	.windowbg2 {
		background: url(data:image/gif;base64,R0lGODlhAQABAJEAAAAAAP///////wAAACH5BAEAAAIALAAAAAABAAEAAAICVAEAOw==) no-repeat !important;
		background-color: #F2F2F2 !important;	
		}

	.catbg3, tr.catbg3 td,  
	.windowbg3 {
		background: url(data:image/gif;base64,R0lGODlhAQABAJEAAAAAAP///////wAAACH5BAEAAAIALAAAAAABAAEAAAICVAEAOw==) no-repeat !important;
		background-color: #E5E5E5 !important;	
		}
	}
