css:
@-moz-document url-prefix("http://baidu.com"), url-prefix("http://www.baidu.com"), url-prefix("https://www.baidu.com"), url-prefix("https://baidu.com") {
.s_down {
    background: rgba(255, 255, 255, 0.95) !important;
}

.c-container {
    background: #fff;
    border-radius: 2px;
    padding: 25px;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    width: 549px;
}

.c-container:hover {
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    box-shadow: rgba(0, 0, 0, 0.188235) 0px 10px 30px, rgba(0, 0, 0, 0.227451) 0px 6px 10px;
}

.c-container:active {
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    box-shadow: rgba(0, 0, 0, 0.188235) 0px 10px 30px, rgba(0, 0, 0, 0.227451) 0px 6px 10px;
}

body {
    background-color: #f5f5f6;
}

.index-logo-src {
    display: none;
}

#head {
    box-shadow: 0 0 10px #00000024;
}

.wrapper_new #s_tab {
    color: #626675;
    padding-top: 80px;
}

em {
    text-decoration: none !important;
}

* {
    font-family: -apple-system, "Helvetica Neue", Helvetica, "Nimbus Sans L", Arial, "Liberation Sans", "PingFang SC", "Hiragino Sans GB", "Source Han Sans CN", "Source Han Sans SC", "Microsoft YaHei", "Wenquanyi Micro Hei", "WenQuanYi Zen Hei", "ST Heiti", SimHei, "WenQuanYi Zen Hei Sharp", sans-serif;
}

a {
    text-decoration: none;
    margin-bottom: 2px;
    color: #4879BD;
}

h3 a:hover {
    border-bottom: #4879BD 1.2px solid;
}


.wrapper_new #form .bdsug-new {
    box-shadow: 0px 10px 10px #00000033;
    border-radius: 0;
    border: 2px solid #4e71f2!important;
    border-top: 0!important;
}

.wrapper_new .s_ipt_wr {
    border: 2px solid #c4c7ce;
    border-radius: 0;
    border-right: 0;
}

.wrapper_new .s_btn_wr .s_btn {
    cursor: pointer;
    width: 112px;
    height: 40px;
    line-height: 0px;
    border-radius: 0;
}

.wrapper_new #form .bdsug-new ul li {
    font-size: 15px;
}

.bdsug-feedback-wrap{
    display: none;
}
}
