.logo {
    margin-top: -15px;
}
.top_logo:after {
    text-align: center;
    margin-top: 30px;
    margin-left: 10px;
    color: #fff;
    content: "Officially Slow";
    display: block;
    font-size: 10px;
    text-transform: uppercase;
    clear: both;
    position: absolute;
    float: left;
}
@media all and (max-width:1024px) {
.logo {
    margin-top: 0px;
}
.top_logo:after {
    display:none;
}
}
@media all and (max-width:800px) {
.logo {
    margin-top: -10px;
}
.top_logo:after {
    display:block;
    margin-top: 25px;
}
}
