css:
@-moz-document domain(gingkoapp.com)
{
.cards
  {
    border-top: 1px solid #111;
    background-color: #ffffff;
    color: #111;
  }

.card.card-active, 
.cards.cards-child  {
    background: #dedee2;
  }

  .card h1,
.card h2,
.card h3,
.card h4
  {
    font-weight: bold;
  }

  input[type=checkbox]::after
  {
    content: "";
    width: 16px;
    height: 16px;
    border: 1px solid #808080;
    border: 1px solid rgba(113, 124, 146, 0.2);
    background: #FFF;
    margin: 0;
    float: left;
    margin-right: 5px;
    margin-top: -2px;
    margin-bottom: -4px;
    margin-left: -6px;
  }

  .checked input[type=checkbox]::after
  {
    content: "";
    background: #3D454B;
    background: #a5abb7;
  }

  .card .todo-list .checked
  {
    text-decoration: none;
    color: gray;
  }
}
