css:
@-moz-document domain("france.tv") {
    .l-body__bg:not(.l-body__bg--dark),
    body:not(.l-body__bg--dark) .c-primary-nav,
    body:not(.l-body__bg--dark) .c-sub-nav {
        background-color: #191e25;
        color: #ccc;
    }
    body:not(.l-body__bg--dark) .c-primary-nav-item__picto {
        filter: invert(1);
    }
    body:not(.l-body__bg--dark) .c-primary-nav-item__link,
    body:not(.l-body__bg--dark) .c-sub-nav-item__link,
    body:not(.l-body__bg--dark) .c-section-header__title {
        color: white;
    }
    body:not(.l-body__bg--dark) .c-primary-nav__logo img {
        filter: invert(1);
    }
    body:not(.l-body__bg--dark) .c-primary-nav.is-opened,
    body:not(.l-body__bg--dark) .is-sticky .c-primary-nav {
        border-bottom-color: #44484E;
    }
    body:not(.l-body__bg--dark) .c-shortcuts,
    .c-breadcrumb-item,
    .c-breadcrumb-item__text {
        color: gray;
    }
    body:not(.l-body__bg--dark) .c-shortcuts-ctn-links-item,
    body:not(.l-body__bg--dark) .c-seo {
        color: #5e5e5e;
    }

    body:not(.l-body__bg--dark) .c-card-video__textarea-title,
    body:not(.l-body__bg--dark) .c-live-card,
    body:not(.l-body__bg--dark) .c-live-card__secondary-link {
        color: #ccc;
    }
    body:not(.l-body__bg--dark) .flickity-prev-next-button {
        background-color: #eedb;
        filter: invert(1);
    }
    body:not(.l-body__bg--dark) .c-live-card {
        background-color: transparent;
    }
    body:not(.l-body__bg--dark) .c-card-video__textarea-subtitle,
    body:not(.l-body__bg--dark) .c-card-video__textarea-description {
        color: #5f7081;
    }

    body:not(.l-body__bg--dark) .c-live-card--france-2,
    body:not(.l-body__bg--dark) .c-sub-nav-item-channel--france-2 .c-sub-nav-item-channel__link {
        background-image: url("https://www.france.tv/images/logos/channels/france-2-invert.svg?v=1");
    }
    body:not(.l-body__bg--dark) .c-live-card--france-3,
    body:not(.l-body__bg--dark) .c-sub-nav-item-channel--france-3 .c-sub-nav-item-channel__link {
        background-image: url("https://www.france.tv/images/logos/channels/france-3-invert.svg?v=1");
    }
    body:not(.l-body__bg--dark) .c-live-card--france-4,
    body:not(.l-body__bg--dark) .c-sub-nav-item-channel--france-4 .c-sub-nav-item-channel__link {
        background-image: url("https://www.france.tv/images/logos/channels/france-4-invert.svg?v=1");
    }
    body:not(.l-body__bg--dark) .c-live-card--france-5,
    body:not(.l-body__bg--dark) .c-sub-nav-item-channel--france-5 .c-sub-nav-item-channel__link {
        background-image: url("https://www.france.tv/images/logos/channels/france-5-invert.svg?v=1");
    }
    body:not(.l-body__bg--dark) .c-live-card--france-o,
    body:not(.l-body__bg--dark) .c-sub-nav-item-channel--france-o .c-sub-nav-item-channel__link {
        background-image: url("https://www.france.tv/images/logos/channels/france-o-invert.svg?v=1");
    }
    body:not(.l-body__bg--dark) .c-live-card--la1ere,
    body:not(.l-body__bg--dark) .c-sub-nav-item-channel--la1ere .c-sub-nav-item-channel__link {
        background-image: url("https://www.france.tv/images/logos/channels/la1ere-invert.svg?v=1");
    }

    .c-now-on-channel__logo {
        background: #89a;
        border-radius: 50%;
        margin-left: 60px;
        padding: 0;
    }
    .c-now-on-channel__card-live-play-icon {
        filter: invert(1);
    }
    .c-now-on-channel__card {
        color: white;
        height: 52px;
        justify-content: center;
    }
    .c-live-progress__bg {
        opacity: 0.4;
    }

    .c-sub-category {
        background: #353d48;
        color: #ccc;
    }
}
