css:
@-moz-document url-prefix("https://www.bing.com/search?") {
body,
#b_results > li {
    background: #fff;
}
/******************  Main Results ****************/
#b_content li {
    display: inline-block;
}
#b_results > li.b_algo {
    padding-top: 20px !important;
    box-shadow: 0px 3px 9px #b0b0b0;
    width: 120%;
    background: #fff;
}

/******************  Right side  context ****************/
#b_context > li {
    position: relative !important;
    left: 30% !important;
    box-shadow: 1px 1px 10px #bfbfbf;
}
/******************  URL NAME ****************/
.b_attribution > cite {
    color: #216e8e !important;
}
/******************  Search Videos ****************/
#b_results > li > #serpvidans {
    width: 75%;
}
/******************  Search Image sliders ****************/
#b_results > li.b_imgans {
    width: 120%;
}

/******************  Search Focus top results ****************/
#b_results > li.b_top {
    width: 120%;
}

.b_slidebar {
    width: 40vw;
}
}

@-moz-document url-prefix("https://www.bing.com/search?") {
/*
* {
    font-family: Google sans, "roboto", tahoma, arial, sans-serif !important;
} 
*/
#b_results > li a:visited {
    color: #9b9b9b;
}
#b_results > li a {
    color: #2b2b2b;
    font-weight: 500;
}

h2,
h2.b_topTitle,
.b_algo .b_deep h3 {
    font-size: 18px;
}
strong {
    color: #1794e1;
    font-style: normal;
    font-weight: 600;
}


p {
    color: #464646;
}
.b_scopebar {
    margin: 25px 0 0 150px;
}
form {
    margin: 10px 0 0 45px;
}
.b_scopebar {
    margin: 20px 0 0 150px;
}
.b_active a {
    color: #de3700 !important;
}
.b_scopebar a {
    color: #777 !important;
}
.b_scopebar li {
    padding: 5px 8px 9px 8px;
    line-height: 22px;
}


.b_searchboxForm,
.b_searchboxForm:hover {
    border-radius: 20px;
}
body .b_searchboxForm .b_searchboxSubmit {
    border: 0px !important;
}

/******************  Paragraph Line in between height ****************/
.b_deep,
#b_results .b_caption,
#b_results .b_snippet,
#b_results .b_attribution,
#b_results .b_factrow,
#b_results p,
#b_context .b_caption,
#b_context .b_snippet,
#b_context .b_attribution,
#b_context .b_factrow,
#b_context p,
#snct {
    line-height: 2.0;
}

/******************  Header ****************/
#b_header {
    background: linear-gradient(#c0c0c0, #f7f7f7);
      box-shadow: 0px 3px 10px #bbb;
}
/******************  Feedback ****************/

}

@-moz-document url-prefix("https://www.bing.com/search?") {
body .b_searchbox {
    background-color: transparent;
}
body .b_searchboxForm {
    background-color: #ffffffb0;
}
body .b_searchboxForm .b_searchboxSubmit {
    border: 8px solid #fff0;
    background-color: #fff0;
}
#b_footerItems,
#b_footer {
    display: none;
}
#focus_ovr {
    height: 100% !important;
}
}
