css:
@-moz-document url-prefix("https://google"), url-prefix("https://www.google"), url-prefix("http://blogsearch.google"), url-prefix("http://209.85.165.104"), url-prefix("http://video.google"), url-prefix("https://encrypted.google"), url-prefix("http://images.google"), url-prefix("https://images.google"), url-prefix("http://books.google"), url-prefix("https://books.google"), url-prefix("http://www.google") {
/* Change the Google logo on the home pages of Google, doodled Google, Google Images, Google Videos */

/* (Optimized for Google home page. Doesn't work correctely with Google Videos home page. You cannot use doodles, even on the Google doodles site.) */
 #lga {
    box-sizing: border-box !important;
    background: url("https://i.imgur.com/aEC0Uw7.gif") center no-repeat !important;
    /*Chr_1.gif*/
    /* ============================================== */
    /* i really want this to be global */
    /* the code used was from the original
      https://userstyles.org/styles/173462/google-hestia-animated
      by Andrei Zhuk*/
    /* Twitter - https://twitter.com/_Asryal */
    /* Patreon - https://www.patreon.com/Asryal */
    /* ============================================== */
    bottom: 200px !important;
    z-index: -999 !important;
    position: relative !important;
    padding-bottom: 700px !important;
    margin: auto !important;
}
/* Remove the text "images" from the Google Images home page. */
 #hplogo {
    text-indent: -9000px !important;
    white-space: nowrap !important;
}
/* Change the Google logo at the top left of the search results page. */

/* (Doesn't change the doodled logo.) */
 #logo {
    background: url("https://i.imgur.com/aEC0Uw7.gif") 50% 80% no-repeat !important;
    /*Chr_3.gif*/
    background-size: contain !important;
    padding-bottom: 80px !important;
    bottom: 70px !important;
}
/* Add Sishu to the bottom right of the search results page. */
 .mw {
    background: url("https://i.imgur.com/aEC0Uw7.gif") 50% 100% no-repeat !important;
    /*Chr_2.gif*/
    /* Change first value here if she is dancing too far left/right. */
    background-attachment: fixed !important;
}
/* Change the background of the Informations section on the search results page. */
 .xpdopen {
    background: white !important;
    /* Change the color if it doesn't match your theme. Example : white color in HTML : "white" or "#ffffff" or "rgb(255, 255, 255) or "hsl(0, 0%, 100%)" */
}
#wp-tabs-container {
    background: white !important;
    /* Change the color if it doesn't match your theme. Example : white color in HTML : "white" or "#ffffff" or "rgb(255, 255, 255) or "hsl(0, 0%, 100%)" */
}
body {
    /*background-image: linear-gradient(rgba(171,47,57,0), rgba(171,47,57,1)) !important;*/
    background-image: linear-gradient(rgba(226, 48, 61, 0), rgba(226, 48, 61, 0)) !important;
}
.sfbg {
    margin: -9000px !important;
    white-space: nowrap !important;
}
}
