@-moz-document domain("cetesdirecto.com") {
    .compMenu, .container, .scrolledPortafolio, .container-fluid, .contenidoPrincipal, .graficaInstrumentos, .contenidoSecundario, .legend, .navbar, .containerPrincipal, .resumenPosicionBody, .detallePosicionContenido, .ui-widget-content, .resumenMovTabBody, body {
    background-color: #2f2f2f;
    }
    
    .container.containerPrincipal {
        background-color: #2f2f2f !important;
    }
    
    .resumenPosicion, .detallePosicion, .resumenPosicionTitulo, #codigoUser, #tablaCodigos table, #tablaCodigos, #menuLateralWeb, form table{
        background-color: #252525 !important;
    }
    
    body, #usuarioNombre, .legendLabel, .tituloEncabezado, .variableEncabezado, .detalleDataLeft, .detalleDataRight, .texto, .ui-widget-content, .ui-selectmenu-text, .menuLateralFonts{
        color: #E1E1E1 !important;
    }

        
    .totalInstrumentos .txtInstrumento, .totalInstrumentos .percentInstrumento, .totalInstrumentos{
    color: #0093ff;
    }
    
    .ui-widget.ui-widget-content {
    border: 1px solid #252525;
    }
    
    .resumenMovTabBody {
        border-color: #252525;
    }
    
    .icon-bar{
        background-color: #E1E1E1 !important;
    }
    
    .logo-menu {
    padding-left: 220px;
    box-sizing: border-box;
    background: url(https://i.imgur.com/jrnNv6Z.png) no-repeat;
    width: 1px;
    background-size: 220px auto;
    }
}
