css:
@-moz-document domain("music.youtube.com") {
    body {
        background-color: #080A30
    }
    ytmusic-detail-header-renderer {
        background-color:#263268;
    }
    ytmusic-nav-bar {
        background:#060723;
    }
    ytmusic-player-bar {
        background:#060723;
    }
    ytmusic-player-page {
        background:#000308;
    }
    html:not(.style-scope) {
        --ytmusic-text-primary: #3ea6ff;
        --ytmusic-text-secondary: #4F6CD6;
        --ytmusic-brand-background-solid: #060723;
        --ytmusic-menu-item-icon_-_fill: #3183C6;
        --ytmusic-inactive-tab: #3183C6;
        --ytmusic-color-youtubered: #3183C6;
        --ytmusic-icon-inactive: #3183C6;
        --ytmusic-color-white1-alpha10: #060723cf;
        --ytmusic-subtitle-2_-_color: #4F6CD6;
        --ytmusic-divider: #1b7fcc2e;
        --ytmusic-detail-header: #263268;
        --yt-endpoint-color: #4F6CD6;
        --yt-endpoint-visited-color: #4F6CD6;
        --yt-spec-call-to-action: #4F6CD6;
    }
    img[src*="//s.ytimg.com/yts/img/music/web/on_platform_logo_dark-vfl_PUy2j.svg"] {
        content: url("https://i.imgur.com/tw9Mxhi.png");
    }
}
