    #owner-and-teaser.style-scope.ytd-watch-metadata,
    #contents.style-scope.ytd-rich-metadata-row-renderer,
    #clarify-box.style-scope.ytd-watch-flexy,
    #container.style-scope.ytd-masthead,
    #items.style-scope.ytd-watch-next-secondary-results-renderer,
    #spinnerContainer.active.style-scope.paper-spinner,
    #title .style-scope.ytd-comments-header-renderer,
    .dropdown-trigger.style-scope.ytd-menu-renderer,
    .style-scope.ytd-menu-renderer.style-default.size-default,
    .yt-simple-endpoint.style-scope.ytd-video-primary-info-renderer,
    .yt-live-chat-placeholder-item-renderer.style-scope.yt-live-chat-item-list-renderer,
    img.branding-img.iv-click-target,
    yt-next-continuation,
    ytd-item-section-renderer,
    ytd-watch-next-secondary-results-renderer,
    ytd-merch-shelf-renderer.style-scope.ytd-watch-flexy,
    ytd-playlist-panel-renderer#playlist.style-scope.ytd-watch-flexy,
    yt-icon.super-title-icon.style-scope.ytd-video-primary-info-renderer,
    ytd-action-companion-ad-renderer.style-scope.ytd-companion-slot-renderer,
    ytd-badge-supported-renderer.style-scope.ytd-video-primary-info-renderer,
    yt-formatted-string.super-title.style-scope.ytd-video-primary-info-renderer,
    #background.style-scope.ytd-masthead,
    #owner.item.style-scope.ytd-watch-metadata,
    ytd-button-renderer.style-scope.ytd-menu-renderer,
    yt-button-shape#button-shape.style-scope.ytd-menu-renderer,
    #super-title.style-scope.ytd-watch-metadata {
        display: none !important
    }

    #top-row.style-scope.ytd-watch-metadata {
        border: none !important
    }

    ytd-watch-metadata.watch-active-metadata.style-scope.ytd-watch-flexy {
        padding-top: 0 !important
    }

    #container.style-scope.ytd-video-secondary-info-renderer {
        display: none !important;
        visibility: hidden !important
    }

    #primary.style-scope.ytd-watch-flexy {
        padding-top: 8px !important;
        padding-left: 9px !important;
        padding-right: 4px !important;
        margin: auto !important
    }

    #chat.style-scope.ytd-watch-flexy,
    #columns #secondary.style-scope.ytd-watch-flexy {
        width: auto !important;
        max-width: 1046px !important;
        padding-top: 0px !important;
        margin-top: 4px !important
    }

    #secondary-inner.style-scope.ytd-watch-flexy {
        min-width: 300px !important;
        max-width: 100% !important
    }

    /* Adjustable width */
    #secondary-inner.style-scope.ytd-watch-flexy:hover {
        min-height: 83.33vh !important;
        max-height: 99.33vh !important;
        resize: horizontal !important;
        overflow: auto !important;
        overflow-y: hidden !important;
        cursor: not-allowed !important;
        direction: rtl !important
    }

    @supports (-webkit-appearance: none) and (not (-ms-ime-align: auto)) and (not (-moz-appearance: none)) {
        #secondary-inner.style-scope.ytd-watch-flexy {
            min-height: 83.33vh !important;
            max-height: 99.33vh !important;
            resize: horizontal !important;
            overflow: auto !important;
            overflow-y: hidden !important;
            cursor: not-allowed !important;
            direction: rtl !important
        }
    }

    #chat.style-scope.ytd-watch-flexy:hover {
        max-height: 91.03vh !important;
        resize: vertical !important;
        overflow: auto !important;
        direction: rtl !important
    }

    ytd-watch-metadata,
    ytd-video-primary-info-renderer {
        opacity: .3 !important;
        transition: 1s all !important;
        -moz-transition: opacity 3s ease-out !important;
        -o-transition: opacity 3s ease-out !important;
        -webkit-transition: opacity 3s ease-out !important
    }

    ytd-watch-metadata:hover,
    ytd-video-primary-info-renderer:hover {
        opacity: 1 !important;
        transition: 1s all !important;
