/* Chat room colours */
/* Author [Default] */
yt-live-chat-author-chip.style-scope.yt-live-chat-text-message-renderer:not([is-highlighted]) #author-name.style-scope.yt-live-chat-author-chip {
    color: /*[[chat-room-a-colour]]*/ !important;
    font-weight: 600 !important;
    margin-left: -3px !important;
    max-width: 300px !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: clip !important;
    display: inline-block !important
}

/* Author [Member] */
yt-live-chat-author-chip.style-scope.yt-live-chat-text-message-renderer:not([is-highlighted]) #author-name.member.style-scope.yt-live-chat-author-chip {
    color: #1E9E2F !important;
    font-weight: 600 !important
}

/* Author [Moderator] */
yt-live-chat-author-chip.style-scope.yt-live-chat-text-message-renderer:not([is-highlighted]) #author-name.moderator.style-scope.yt-live-chat-author-chip {
    color: #3E90FE !important;
    font-weight: 600 !important
}

