#message.style-scope.yt-live-chat-text-message-renderer:not([is-highlighted]) {
    font-weight: 500 !important
}

#input-container .style-scope.yt-live-chat-message-input-renderer[is-highlighted] #author-name.style-scope.yt-live-chat-author-chip {
    color: #111111 !important
}

yt-live-chat-author-chip.style-scope.yt-live-chat-text-message-renderer::before {
    font-weight: 500 !important;
    width: 0rem !important;
    padding-left: 1px !important;
    content: ":" !important;
    direction: ltr !important
}

yt-live-chat-author-chip.style-scope.yt-live-chat-text-message-renderer {
    flex-direction: row-reverse !important
}

#chat-badges > .style-scope.yt-live-chat-author-chip {
    padding: 0 6px 0 0 !important;
    margin-left: 0px !important
}

#chat-messages #message {
    margin-left: 1px !important
}

/* Deleted */
yt-live-chat-text-message-renderer[is-deleted] {
    opacity: .5 !important
}

yt-live-chat-text-message-renderer[is-deleted]:hover {
    opacity: 1 !important
}

img.branding-img.iv-click-target {
    opacity: 0 !important;
    transition: 1s all !important
}

img.branding-img.iv-click-target:hover {
    opacity: 1 !important;
    transition: .5s all !important
}

#message.style-scope.yt-live-chat-text-message-renderer {
    display: unset !important;
    font-style: normal !important
}

#deleted-state.style-scope.yt-live-chat-text-message-renderer:before {
    content: ' ' !important
