css:
@-moz-document url("https://web.hike.in/") {
    :root {
        --hike-accent: #00ACFF !important;
        --background-color: #2A2B36 !important;
        --background-dark: #1F2028 !important;
        --text: #E8F3F7 !important;
    }


    .a4_b, .kk_k1 {
        background-color: var(--background-color) !important;
    }


    .fx_fy .fx_gg{
        background-color: var(--background-color) !important;
    }

    .li_lj .li_kq{
        background-color: var(--background-dark) !important;
    }

    .li_lj .li_lk{
        background-color: var(--background-dark) !important;
    }

    .li_lj .li_kq .li_kr{
        color: var(--text) !important
    }

    .li_lj .li_lm .mdc-form-field .li_ln{
        color: var(--text) !important
    }

    .li_lj .li_lm .mdc-form-field .li_lo{
        color: var(--text) !important
    }

    .kk_k8 .kk_la{
        color: var(--text) !important
    }

    .mdc-list-item__primary-text{
        color: var(--text) !important
    }

    .mdc-list-item__secondary-text{
        color: grey !important
    }

    .fx_gi .fx_gj header.mdc-top-app-bar .mdc-top-app-bar__row .mdc-top-app-bar__section .fx_gl{
        background-color: var(--hike-accent) !important;
        border-radius: 15px;
    }


    body>div:nth-child(2){
        width: 100%;
        height: 90%;
    }

    .a4_b .a4_a5 .a4_a7 .a4_a9{
        background-color: var(--background-color) !important
    }

    .g0_g1{
        background-color: var(--background-color) !important
    }


    .m7_na, .m7_ni, .gp_l0, .mdc-card{
        background-color: var(--background-dark) !important
    }

    .b7_co, .b7_cp, .b7_ca{
        background-color: var(--background-dark) !important;
        color: var(--text) !important
    }

    .b7_cs, .b7_ct, .b7_ee{
        color: var(--text) !important
    }

    .m7_m9 .m7_ni .m7_nl .m7_nm{
        color: var(--text) !important
    }

    .gp_l0 .mdc-top-app-bar__title .gp_l1{
        color: var(--text) !important
    }

    .mdc-top-app-bar .mdc-top-app-bar__action-item, .mdc-top-app-bar .mdc-top-app-bar__navigation-icon, .mdc-top-app-bar__title{
        color: var(--text) !important
    }

    .g0_g1 .g0_g2 .g0_hc .g0_i1.g0_jh .g0_hl .g0_ii{
        color: var(--text) !important
    }

    .g0_g1 .g0_g2 .g0_hc .g0_i1.g0_jf.g0_jh{
        background-color: #FFFFFF !important
    }

    .g0_g1 .g0_g2 .g0_hc .g0_hf .g0_hh{
        color: var(--text) !important
    }

    .g0_g1 .g0_g2 .g0_g6.g0_g8{
        background-color: slategrey !important;
        color: var(--text) !important
    }

    .mdc-list, .gp_gq, .gp_gs{
        background-color: #1F2028 !important;
        color: var(--text) !important
    }

    .b7_cb .b7_cc{
        color: var(--text) !important
    }


    .fx_fy .fx_f0 .mdc-list-item__text .mdc-list-item__primary-text{
        color: var(--text) !important
    }

    .fx_fy .fx_gg .fx_gh *{
        color: skyblue !important
    }


    .g0_g1 .g0_g2 .g0_hc .g0_i1.g0_jf.g0_jh{
        background-color: steelblue !important
    }

    .g0_g1 .g0_g2 .g0_g6.g0_g7{
        background-color: khaki !important
    }

    ::-webkit-scrollbar {
        width: 12px;
    }

    ::-webkit-scrollbar-track {
        -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
        border-radius: 10px;
        background-color: var(--background-color) !important
    }

    ::-webkit-scrollbar-thumb {
    	border-radius: 10px;
        -webkit-box-shadow: inset 0 0 6px var(--hike-accent); 
    }

}
