    color: white;
}
.faq .item .item-header:hover .arr-top {
    fill: #001eff;
}
p {
    color: #0014aa;
}
a{
    color: #9800ff;
}
::-webkit-scrollbar-thumb {
    background-color: #424242 !important;
    box-shadow: none;
}
::-webkit-scrollbar {
    background-color: rgb(9, 9, 9) !important;
}
}

@-moz-document url("https://www.multcloud.com/tutorials/index.html") {
.toggle {
    background-color: black;
}
.toggle-content-item dd h4, .toggle-content-item dt .left h3 {
    color: white;
    text-shadow: 0 0 15px white;
}
p, .message ul li{
    color: #9a9a9a9a;
}
.icon-Tra:before, .icon-Backup:before, .icon-Mgt:before, .icon-Offline:before {
    color: #000b5d;
}
span.font-size20 {
    text-shadow: 0 0 5px #141c57;
}
.btn.btn-blue {
    background-color: #000e77;
    border-color: #000e77;
    box-shadow: none;
}
.btn.btn-blue:hover {
    background-color: #0014ac;
    border-color: #0014ac;
    box-shadow: 0 0 20px #0014ac;
}
.toggle .toggle-head-item {
    border-bottom: 1px solid #000b5d;
}
.media .media-items .media-item.media-item-leftRight {
    border-bottom: 1px solid #000b5d;
}
.arrow-box .bottom .bottom-arrow1, .arrow-box .bottom .bottom-arrow2 {
    border-top: 10px #000b5d solid;
}
.media .media-items .media-item .item-img {
    filter: hue-rotate(45deg) saturate(15);
}
.link, .more-blue {
    color: #001080;
}
.toggle-content-item dt {
    background-color: #000b5d;
}
body > div.toggle > div > div > ol > li:nth-child(1) > p {
    filter: invert(1) hue-rotate(180deg) saturate(1.5);
}
::-webkit-scrollbar-thumb {
    background-color: #424242 !important;
    box-shadow: none;
}
::-webkit-scrollbar {
    background-color: rgb(9, 9, 9) !important;
}
}

@-moz-document url("https://www.multcloud.com/cloud-transfer/index.html") {
.youtube-video, .posts {
    background-color: black;
}
h4,.youtube-video h4, .posts li p, .font-size24 {
    color:white;
    text-shadow: 0 0 10px white;
}
p {
    color: #9a9a9a;
}
.articles-chunk {
    background-color: #111;
}
.articles-chunk p a {
    color: #0014a8;
    transition: all 0.5s;
}
.articles-chunk p a:hover {
    color: #001eff;
    text-shadow: 0 0 10px #001eff;
    transition: all 0.5s;
}
.articles-chunk .light-theme .disabled .current, .articles-chunk .light-theme a {
    background: #000;
    border-color: #000;
}
.articles-chunk .light-theme a:hover {
    color: #000d9b;
    background-color: #000;
}
.articles-chunk .light-theme .active .current {
    background: #040082;
    color: #fff;
    border-color: #040082;
}
::-webkit-scrollbar-thumb {
    background-color: #424242 !important;
    box-shadow: none;
}
::-webkit-scrollbar {
    background-color: rgb(9, 9, 9) !important;
}
}

@-moz-document url("https://www.multcloud.com/about") {
.bg-base {
    background-color: #000;
}
h2 {
    color: white;
    text-shadow: 0 0 10px white;
}
p {
    color: #9a9a9a;
}
.media.media1 .media-items .media-item .item-img {
    filter: hue-rotate(43.5deg) saturate(24) brightness(1.3);
}
body > div.media.media1.bg-base.about-contact {
    background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(9,9,121,1) 35%, rgb(0, 73, 88) 100%);
}
.about-contact ul li {
    color: white;
}
body > div.media.media1.bg-base.about-contact > div > div > ul > li:nth-child(1) > a{
    color: #00cde3;
}
::-webkit-scrollbar-thumb {
    background-color: #424242 !important;
    box-shadow: none;
}
::-webkit-scrollbar {
    background-color: rgb(9, 9, 9) !important;
}
}

@-moz-document url("https://www.multcloud.com/") {
::-webkit-scrollbar-thumb {
    background-color: #424242 !important;
    box-shadow: none;
}
::-webkit-scrollbar {
    background-color: rgb(9, 9, 9) !important;
}
}
