css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.yourtv.com.au/guide/) {

body{
background:black !important;
}

/* Guide */
.general
{
background:black !important;
color:white !important;
}

/* TV Stations */
.general .grid td.venue
{
background:#310505 !important;
color:#808080 !important;
}

/* Region */
.general .grid td.region
{
background:#250207 !important;
color:#808080 !important;
}

/* Time1 */
.general .grid td.time
{
background:#250207 !important;
color:#808080 !important;
}

/* Time2 */
.general .grid td.time2
{
background:#250207 !important;
color:#808080 !important;
}

/* Date */
.general .grid th.date
{
background:#250207 !important;
color:#808080 !important;
}

/* Table and Pop-up Background */
table
{
background:black !important;
color:#808080 !important;
}


a{

color:#700000 !important;

}

a:visited{

color:#6f5454 !important;

}

a:hover{

text-decoration:none !important;

color:#a00000 !important;

}

.nav .on{

color:#000000 !important;

}



/* hide crap */

#siteTools, 

#CommonPromoAcrossYourTime,

#register-container,

#msnbtmbanner,

.FIXBOTTOM

{

display:none !important;

}

#_restofday{

display:block;

}

#stripAd{

display:none !important;

}

.printHide{

height:0px !important;

}

#footer-new{

display:none !important;

}

#ninemsn-footer{

display:none !important;

}

#preheader-ninemsn-container{

 display:none !important;

}

#header{

 display:none !important;

}

#pageTitle{

 display:none !important;

}



/* stretch content */

#variablewidth{

width:100% !important;

}

#table_container{

width:100% !important;

}

#header a{

display:block !important;

z-index:5 !important;

}

}
