css:
@-moz-document url-prefix("https://s.to")
{
.main-header {
    background-image: linear-gradient(to right,#243743 50%,#243743 50%);
}

.logo-wrapper {
    background: #243743
}

.container>p {
    border: 4px solid #ed781e !important;
}

.shoutbox {
    background: #19262e;
    box-shadow: 0 1px 3px #243743;
}

.shoutboxBodyForm {
    box-shadow: 0 -1px 17px #121c22;
}

.shoutboxBodyForm .button {
    bottom: 125px;
}

textarea {
    background: #243743;
    border: 1px solid #000;
    color: #DCDCDC;
}

textarea:focus {
    border: 1px solid #ed781e;
}

.shoutboxMessages {
    height: 655px;
}

.shoutboxMessage {
    border-bottom: 1px solid #243743
}

.shoutboxMessage strong {
    color: #ed781e !important;
}

.shoutboxMessage p, .darkColor {
    color: #C9C9C9 !important;
}

div.shoutboxIcons {
    height: 50px;
}

.shoutboxUserOnline {
    color: #DCDCDC;
}

div.col-md-10.shoutboxMessageContent>ul>li>a {
    background: #ed781e !important;
}

.col-md-2 img:hover, #series .title .picture img:hover {
    -ms-transform: scale(1.30);
    -moz-transform: scale(1.30);
    -webkit-transform: scale(1.30);
    -o-transform: scale(1.30);
    transform: scale(1.30);
    -ms-transition: all .5s;
    -webkit-transition: all .5s;
    -moz-transition: all .5s;
    -o-transition: all .5s;
    transition: all .5s;
}

::-webkit-scrollbar-track-piece
  {
    background-color: #212121;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important;
    border-radius: 0 !important;
    border: none !important;
  }

::-webkit-scrollbar {
    background-color: rgba(62, 62, 62, .92);
    border: 1.5px solid rgb(62, 62, 62);
    width: 15px;
}

::-webkit-scrollbar-thumb {
    background-color: rgba(31, 31, 31, 1);
    border: 1.5px solid rgb(62, 62, 62);
}
}
