css:
@-moz-document url-prefix("https://hardverapro.hu") {
body{
    background-color: black;
    color: #c0c0c0;
}
#uad-upload-zone .upload-text,
.dropdown-item:hover,
.uad-bundle{
    color: black;
}
#page-index,
#page-forum,
#page-full,
#page-split{
    background-color: black;
}
#page-index .container,
.table{
    background-color: #222 !important;
    color: #c0c0c0 !important;
}
#page-forum .container{
    background-color: #223 !important;
}
#page-forum .list-group,
.card-footer,
.content-list{
    background-color: #222 ;
}
#page-forum .list-group a,
.thread-list .media a,
.thread-list .media button,
.breadcrumb a{
    color: #c0c0c0;
}
.thread-list .thread-list-header{
    background-color: #222;
    color: #c0c0c0;
}
.thread-list .media{
    background-color: #444;
}
.thread-list .media:nth-of-type(odd){
    background-color: #555;
}

.modlinks label,
.modlinks .btn,
.modlinks .btn:hover{
    color: #c0c0c0 !important;
}

.card-body,
.card-forum,
.card-header,
#foot{
    background-color: #222 !important;
    color: #e4e4e4 !important;
}
.uad-content-block{
    background-color: #222 !important;
    color: #e4e4e4 !important;
    border-bottom: 1px solid black !important;
}
.uad-categories-item > a{
    color: #e4e4e4;
}
.dropdown-menu .dropdown-item.active{
    background-color: #0d0;
    color: black !important;
}
#uad-images-carousel,
.uad-details,
.uad-actions{
    background: #223 !important;
    border-bottom: 1px solid black !important;
    border-radius: 0px !important;
}

.uad-title > p,
.uad.iced h1,
.fa-snowflake{
    color: #8ce !important;
}

.modal-content{
    background-color: #222;
}
.modal-header{
    border-bottom: 1px solid black;
}

.nav-link,
.nav-link:hover,
.table>tbody>tr>td.cell-face a,
.table>tbody>tr>td.cell-face a:hover,
.table-responsive a,
.dropdown-menu > h6 a,
.uad-list a,
.msg-list a,
.msg-list .card .card-body .media-body a,
.uad-details a,
.mb-3 > h6 a,
#right .card-body a,
.content-list a,
.modal-content a{
    color: #0d0;
}

.uad h1,
.uad-content-block table a,
.uad-actions > div:first-child > span a,
.active > a,
.uad-price,
.card-header a,
.user-thread-list-hovericon,
.text-md-left,
.list-group-item a:hover{
    color: #0d0 !important
}

.msg-list .jumbotron{
    background-color: #223;
    color: #c0c0c0;
}

.user-thread-list-sortable a{
    color: #c0c0c0 !important;
}

.btn-primary,
.btn-secondary{
    background-color: #0d0 !important;
    border-color: #0d0 !important;
    color: #fff !important;
}
.btn-primary:hover,
.btn-secondary:hover{
    background-color: #0c0 !important;
    border-color: #0c0 !important;
}
.carousel-control-expand{
    color: #c0c0c0 !important;
    opacity: 1 !important;
}

.uad-list,
.uad-content,
.list-message{
    background-color: #222;
    color: #c0c0c0;
    border-bottom: 1px solid black !important;
}

.uad-title > h1 a,
.uad-light,
.uad-light a{
    color: #c0c0c0 !important;
}

.featured{
    background-color: #223 !important;
}
.media{
    border-bottom: 1px solid #000 !important;
}
.uad-info{
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}
.list-unstyled > li > h3{
    color: #0d0;
    border-bottom: 1px solid #000 !important;
}
.uad-image{
    border: 1px solid #000 !important;
}
.form-control{
    background-color: #bbb !important;
    border: 1px solid #bbb;
    color: black !important;
}
.input-group-append > button:first-child{
    background-color: #bbb !important;
    border: 1px solid #bbb !important;
    color: black;
}
.navbar{
    background-color: #0d0;
}
.dropdown-menu{
    background-color: #222 !important;
    color: #c0c0c0;
}
.dropdown-divider{
    border-top: 1px solid black;
}
.dropdown-menu .dropdown-item{
    color: #c0c0c0 !important;
}
.dropdown-item:hover{
    background-color: #0d0 !important;
    color: #fff !important;
}
.uad-rating-positive{
    color: #ff4500;
}
.uad-rating-negative{
    color: #7193ff;
}

.btn-light {
    background-color: #0d0;
    border-color: #0d0;
    color: #fff;
}

.msg-list .card .card-header{
    border-bottom: 1px solid #c0c0c0;
    border-top: 1px solid #c0c0c0;
}
.msg-list .msg-featured .card .card-header{
    background-color: #0d0 !important;
    border-color: #c0c0c0;
    color: black !important;
}
.msg-list .msg-featured .card .card-header a{
    color: black !important;
}
.msg-list .msg-featured .card .card-body{
    background-color: #334 !important;
}
.rtif-formats{
    background-color: #222;
}
}
