css:
@-moz-document domain("smash.gg") {
/* 
Last updated Aug 2020
work in progress
mawwwk#1068
*/

:root {
    --bg-dark: hsl(222, 17%, 22%);
    --bg-dark-2: hsl(222, 17%, 29%);
    --bg-dark-2b: hsl(222, 17%, 30%);
     --bg-dark-2c: hsl(222, 17%, 33%);
    --bg-dark-3: hsl(222, 17%, 36%);
    --text-field-box: hsl(222, 17%, 43%);
    --border-color: hsl(222, 12%, 37%);
    --border-color-2: hsl(222, 12%, 53%);
    --text-white: hsl(222, 14%, 85%);
    --text-white-2: hsl(222, 14%, 76%);
    --blue-url: hsl(208, 100%, 71%);
}

body {
    background-color: var(--bg-dark);
    color: var(--text-white);
}

a:-webkit-any-link, .sgg3Kw9L.sgg3DBTA, .sgg3Kw9L.sgg3DBTA:hover {
    color: var(--blue-url);
}

/* Tourney name header */
.regionWrapper-APP_TOURNAMENT_PAGE_WITH_BANNER-Banner, .sgg2Fv5m .sggAWKgK, .sgg2Fv5m, .tourney-content, .sgg23wWo .sgg2_2Pg, .sgg23wWo .sgg3mnWz {
    color: var(--text-white);
    background-color: var(--bg-dark);
}

/* Text headers */
.profileLayout.primaryTab .markdown-container h1, .profileLayout.primaryTab .markdown-container h2, .profileLayout.primaryTab .markdown-container h3, .profileLayout.primaryTab .markdown-container h4, .profileLayout.primaryTab .markdown-container h5, .profileLayout.primaryTab .markdown-container h6, .gg-form-box h1, .gg-form-box h2, .gg-form-box h3, .gg-form-box h4, .gg-form-box h5, .gg-form-box h6, h1, h2, h3, h4, h5, h6 {
    color: hsl(222, 0%, 97%);
}

/* Background */
.profileBelowHeader, .sgg2Qglf, .sgg1apK4, .profileLayout:not(.usesJsScroller) .admin-page-header, .flex-pane-container .flex-pane-main {
    background-color: var(--bg-dark);
}

/* Event sidebar */
.sggidOT8, .desktopProfileNav .tabletUpNavTrack, .sgg1Do28.sgg1-zJm:before, .admin-page-header, .flex-pane-container .flex-pane-affix {
        background-color: var(--bg-dark-2);
}

/* Sidebar > events */
.sgg1yOFc, .sgg1yOFc:active, .sgg1yOFc:hover  {
    background-color: var(--bg-dark-2);
}

/* Event name (Melee, Brawl, etc) */
.sgg1yOFc a.sgg3TG_H .sgg__om6 .sgg3g3Br .sgg2pphX, .sgg1yOFc:active, .sgg1yOFc:hover, .sgg3Kw9L.sgg2RBnw, .sgg3Kw9L.sgg2RBnw:hover{
    color: var(--text-white);
}

/* Event listings on tourney page */
.sgg2DEbQ, .sgg2DEbQ .sgg2NOuX .sgg2RaXF .sgg1tPMW {
    background-color: var(--bg-dark-2);
    color: var(--text-white);
}

/* Individual event page header */
.regionWrapper-APP_TOURNAMENT_PAGE-GlobalNav-MuiTypography-h4, .regionWrapper-APP_TOURNAMENT_PAGE-GlobalNav-MuiTypography-h5 {
    color: var(--text-white);
}
.sgg2wMdK.sgg3GKnQ.sgg1lI09 .sgg2BjhO, .sggoAjjP .sgg1sD7J, .sggoAjjP .sggXOKua {
    background: var(--bg-dark);
    color: var(--text-white);
}

/* Entrants */
.sgg1kPUs, .sgg15Vs9 table th, .regionWrapper-APP_TOURNAMENT_PAGE-FeatureCanvas-gamerTag-makeStyles-jss117 {
    background: var(--bg-dark-2);
    color: var(--text-white);
}

/* Sidebar options text (Home, Attendees, ...) */
.sgg1Ff3D, label {
    color: var(--text-white-2) !important;
}

/* Sidebar options fill color */
.sgg1OAFg .sgg13k6s.sgg22Qh1, .sgg1OAFg .sgg13k6s:hover {
    background-color: var(--bg-dark-3)
}
/* Sidebar icons hover (gamepad etc) */
.sgg1OAFg .sgg13k6s .sggxbi5m:hover .sgg2p_3v {
    color:hsl(0, 0%, 68%);
}
/* Sidebar icons selected */
.sgg1OAFg .sgg13k6s.sgg22Qh1 .sggxbi5m .sgg2p_3v {
    color:#8e8e8e;
}

/* Tourney dashboard */
.profileLayout .page-section.page-section-grey, .adminContents {
    background-color: var(--bg-dark);
}
.appEntry-MuiDrawer-root {
    background-color: hsl(222, 17%, 20%);
}

/* Box background (e.g. registration settings Social Connections) */
.sgg2trFL.sggXgbgc {
    background-color: hsl(222, 19%, 32%);
    border-color:var(--border-color-2);
}
/* Text fields */
.regionWrapper-APP_TOURNAMENT_PAGE-FeatureCanvas-MuiOutlinedInput-root, .sgg3NREJ .sgg1mzlH, .gg-form-box {
    background-color: var(--text-field-box);
    color: hsl(0, 0%, 90%);
}
.regionWrapper-APP_HOMEPAGE-GlobalNav-MuiTypography-colorTextSecondary {
    color: hsl(222, 12%, 75%);
}

/* Borders in main tourney page */
.sgg2RaXF .sgg39Cf8:before, .sgg2RaXF .sggEEhSi .sggBYbiy:before {
    background-color: var(--border-color);
}

/* Player tags in event page */
.regionWrapper-APP_TOURNAMENT_PAGE_WITH_BANNER-GlobalNav-gamerTag-makeStyles-jss261 {
    color: var(--text-white);
}

/* Text field (uneditable), i.e. "Short slug smash.gg/" */
.regionWrapper-APP_TOURNAMENT_PAGE_WITH_BANNER-GlobalNav-MuiTypography-colorTextSecondary {
    color: var(--text-white-2);
}
/* Grey text (time zones, player names etc) */
.regionWrapper-APP_TOURNAMENT_PAGE-FeatureCanvas-MuiFormHelperText-root, .regionWrapper-APP_TOURNAMENT_PAGE_WITH_BANNER-GlobalNav-nameSection-makeStyles-jss240 {
    color:hsl(222, 17%, 67%);
}

/* Yellow warning text (e.g. region-restricted tourney text) */
.regionWrapper-APP_TOURNAMENT_PAGE-FeatureCanvas-alertText-makeStyles-jss504.warning {
    color:hsl(50, 100%, 45%);
}

/* Green text */
.text-success {
    color:hsl(121, 32%, 63%);
}
/* Form box ("Waves") */
.gg-form-box {
    background-color: var(--bg-dark-2);
}

/* Yellow box overlay (i.e. Bracket Setup active box)*/
.bracket-setup-page .phase-setup-list .gg-form-box-section.active, .bracket-setup-page .phase-setup-list .gg-form-box-section.editing {
    background-color:var(--bg-dark-3);
} 

/* Inner box i.e. tiebreaker rules */
.bottom-spacer-sm, .attendee-card-event-body, .pagination-wave-tabs li, .tipped-off-timer .splash-timer h3 {
    background-color: var(--bg-dark-3);
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: var(--bg-dark-2b);
}

.regionWrapper-APP_TOURNAMENT_PAGE-GlobalNav-gamerTag-makeStyles-jss160 {
    color: var(--text-white);
}
.gg-list-group>*:last-child {
    background-color: var(--bg-dark-3);
    border-color: var(--border-color);
}

/* Seed generator and misc tables*/
.table .table, .shadow-box, .wave-group-list.full-width .phase-seeding-group-item .seed-panel-body {
    background-color:var(--bg-dark-3);
}

/* Blue notice bar ("Looking to customize your homepage..." */
.regionWrapper-APP_TOURNAMENT_PAGE-FeatureCanvas-root-makeStyles-jss5 {
    background-color: hsl(222, 18%, 38%);
    border-top-color: hsl(222, 18%, 59%);
    color: var(--text-white);
}

/* Circle border around pfp */
.regionWrapper-APP_TOURNAMENT_PAGE_WITH_BANNER-GlobalNav-avatar-makeStyles-jss66 {
    border: 1px solid var(--bg-dark);
}

/* "Not publically visible" lock */
.sgg25w2g {
    color: hsl(208, 60%, 65%);
}

/* Image upload box */
.sggZ4yaW .sgg2VBor, .sggZ4yaW .sggIlIQ7 {
    background: var(--bg-dark);
    color: hsl(360, 81%, 66%);
}

.gg-form-box-section .gg-list-group .gg-list-group-item {
    background-color: var(--bg-dark-2);
        color:var(--text-white);
}

/* Page buttons */
.sgg1u0BK .sgg2vKef .sggkdq1U.sgg2QvM9, .sgg1u0BK .sgg2vKef {
    color: var(--text-white);
    background: var(--bg-dark-2)
}


/* "Edit" big overlay (e.g. registration settings) */
.item-overlay, .gg-list-group-item-overlay {
     color: hsla(222, 100%, 30%, .76);
}
/* Blue button ("Add admin") */
.regionWrapper-APP_TOURNAMENT_PAGE-FeatureCanvas-MuiButton-outlinedPrimary {
    color: var(--blue-url);
}
/* Border color in "Details" WIP */
.sgg1k35w.sgg30sfD+.sgg1k35w.sgg30sfD, .sgg1k35w.sgg30sfD+.sgg1XZEH, .sgg15V3Y+.sgg1k35w.sgg30sfD, .sggnakGZ {
    border-color: var(--border-color) !important;
}

/* Events card */
.Card, .ObjectCard, .EventCard, .MatchCard, .EventCardV2, .TournamentCard, .TournamentEventCard, .ResultsCard, .ShopCard, .CircuitCard, .DeckSummaryCard, .LoadingCard, .PoolCard, .FantasyCard, .NotifCard, .HelpCard {
    background-color: var(--bg-dark-2);
}

/* Tournament set "card" */
.gg-card, .gg-card-body.gg-card-body-grey {
    background-color:hsl(222, 17%, 37%);
}
/* Card player fill */
.match .match-player, .rr-match .match-player {
    background-color: var(--bg-dark-2);
}
/* Card borders */
.match>.match-affix-wrapper>.match-section, .rr-match>.match-affix-wrapper>.match-section {
    border-color: var(--bg-dark-2);
}
/* Tourney set card text */
.gg-card, .match .match-player.entrant, .rr-match .match-player.entrant {
    color: var(--text-white);
}
}
