css:
/* freefeed.net farsi style
V6.00 96/01/23
By : Mohammad Mirzaie
*/
@-moz-document domain("freefeed.net")
{
 @font-face
  {
    font-family: iransans;
  }

  body
  {
    font-family: iransans;
    background-size: auto;
    background-attachment: fixed;
    float: none;
    direction: inherit;
  }

  .comments
  {
    direction: rtl !important;
    font-size: 12px;
    line-height: 23px !important;
  }

  .post .post-body .post-text
  {
    direction: rtl !important;
    font-size: 14px;
    line-height: 24px !important;
  }

  .post .post-body .post-header
  {
    direction: rtl !important;
    font-size: 14px;
    line-height: 24px !important;
  }

  .post-textarea, .comment-textarea
  {
    direction: rtl !important;
  }

  .post-author
  {
    direction: rtl !important;
  }

  .pull-right
  {
    float: right !important;
  }

  .post .post-userpic
  {
    float: right !important;
  }

  .profile .description
  {
    font-family: iransans;
    line-height: 23px !important;
  }

  .post .post-body
  {
    margin-right: 63px;
  }

  .posts
  {
    direction: rtl !important;
    font-size: 14px;
    line-height: 24px !important;
  }

  .post-textarea
  {
    font-family: iransans !important;
    direction: rtl;
  }

  .post-via-sources
  {
    direction: ltr !important;
    margin-right: 40px;
  }

  .post-timestamp
  {
    direction: ltr !important;
  }

  .likes .icon
  {
    float: right !important;
    font-size: 14px;
    margin-left: 5px;
    line-height: 23px !important;
  }

  .post-lock-icon
  {
    float: right !important;
    margin-left: 5px;
  }

  .post-footer
  {
    direction: ltr;
    text-align: right;
  }

  .comment .comment-body
  {
    word-wrap: break-word;
    margin-left: auto;
    margin-right: 19px;
  }

  .comment .comment-icon
  {
    float: right !important;
  }

  .comment .comment-time
  {
    float: right;
  }

  .post-editor .post-textarea
  {
    direction: rtl !important;
    font-size: 12.5px;
    line-height: 23px !important;
  }

  .post-editor .post-edit-actions
  {
    text-align: left !important;
    margin-bottom: 12.5px;
    line-height: 23px !important;
  }

  .post-editor .post-edit-options
  {
    float: right !important;
    margin-bottom: 12px;
  }

  .post-footer{
    font-size: 12px;
	color: #ccc;
  }
  .post-footer-icon{
    display: none;
  }
  
  .p-break{
  height: 1.35em;
  }
  .col-md-3 *, .sidebar, box *, .search-input, .box-header-timeline
  {
    font-family: iransans;
  }
}
