css:
@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/") {
/*此部分为用户自设定部分*/
p,font,tr,td,a,caption,center{
    color:white;
}
a:visited{
     color:white;
}
a:link{
     color:white;
}
A.list2_link{
    color:white;
}
A.list2_link:link{
    color:white;
}
A.list2_link:visited{
    color:white;
}

A.list_link{
    color:white;
}
A.list_link:link{
    color:white;
}
A.list_link:visited{
    color:white;
}
#eeee77 {
    background: #121617;
}
body {
    background: #1f282d;
}
table tbody th {
    background:#868686;
}
center > table > tbody > tr > td > p > font {
    color:#fff;
}
#eeee77 a:hover {
    background-color:#7a807e;
}
/*以上是设定部分，下面请勿乱动*/
/*代码2部分还有可修改部分*/
* {
    margin: 0;
    padding: 0;
}
#eeee77 {
    width: 70px;
    box-shadow: 0px 8px 15px #a2a2a2;
}
#eeee77 a {
    font-family: none;
    font-size: 20px;
    display: inline-block;
    width: 70px;
    text-align: center;
    height: 50px;
    line-height: 50px;
}

#eeee77 br {
    line-height: 2px;
}
#eeee77 br:nth-child(1) {
    line-height: 70px;
}
body > center:nth-child(1) {
    box-shadow: 0px 8px 15px #a2a2a2;
    border: none;
}
body > hr {
    display: none;
}
center > table > tbody > tr > td > p > font {
    font-family: none;
    font-size: 40px;
    line-height: 90px;
    font-weight: bold;
}

body > center {
    margin-bottom: 50px;
}
table center p > font {
    font-size: 15px;
}

table tbody th {
    color: white;
}
form {
    margin-bottom: 30px;
}
body > table center > p font{
    color:#fff;
}
body > table center > p font:after{
    content:"感谢使用由Haraki开发的H2WOJ.";
    line-height:40px;
    color:#fff;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/status.php"), url-prefix("http://www.ssoier.cn:7077/wst/statusx.php") {
/*Luogu调色款*/
.ac font,.ac{
    color: #52C410;
}
.ce font {
    color: #FADB14;
}
.re {
    color: #9D3DCF;
}
.wa,.wa font{
    color: #ff1900;
}
.tle,
.mle,
.ole {
   color: #052242;
}
.wa font:before {
    content: "WA ";
}
.ac font:before {
    content: "AC ";
}

.ce font:before {
    content: "CE ";
}
center>center table tr td:nth-child(2){
    text-align:center;
}
.runid_link:hover{
    color: #50d6c2;
}
}

@-moz-document url("http://www.ssoier.cn:7077/wst/"), url("http://www.ssoier.cn:7077/wst/index.php") {
body > table p font {
    color: #fff;
    font-weight: bold;
}
body > table p font:before {
    content: "「 ";
}
body > table p font:after {
    content: " 」";
}
body > table center > p font {
    color: #fff;
    font-weight: normal;
}
body > table center > p font:before {
    content: "";
}
body > table center > p font{
    color:#fff;
}
body > table center > p font:after{
    content:"感谢使用由Haraki开发的H2WOJ.";
    line-height:40px;
    color:#fff;
}
/*login*/
form table {
    margin: 0 auto;
}
form table table tbody tr td {
    font-size: 25px;
    font-family: none;
}
form table tr td input{
    width:17em;
}
form>table{
    width:400px;
}
form table tr:nth-child(3) input{
    border:0;
    font-size:15px;
    width:4em;
     padding:7px;
    border-radius:7px;
}
form table tr:nth-child(3) a{
    color:white;
    background: #50d6c2;
    font-size:15px;
    padding:10px;
    width:4em;
    border-radius:7px;
}
form table tr:nth-child(3) input:hover{
        box-shadow: 0px 8px 15px #a2a2a2;
}
form table tr:nth-child(3) a:hover{
        box-shadow: 0px 8px 15px #a2a2a2;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/problem_show.php?") {
.pcontent h3 {
    font-size: 20px;
    line-height: 50px;
}
.xxbox p {
    line-height: 30px;
}
body > center:nth-last-child(7),
body > center:nth-last-child(6) {
    box-shadow: 0px 8px 15px #a2a2a2;
    border: none;
}
/* P.S:这个6,7的由来非常之神奇 我一个一个试的*/
}

@-moz-document url("http://www.ssoier.cn:7077/wst/ranklist.php") {
body > center {
    margin-bottom: 30px;
}
caption {
    margin-bottom: 20px;
}
caption a:before {
    content: "User ";
}

}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/problem_list.php") {

}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/mail_index.php"), url-prefix("http://www.ssoier.cn:7077/wst/mail_send.php"), url-prefix("http://www.ssoier.cn:7077/wst/mail_send_result.php") {
body>center:nth-last-child(3) {
    font-size:25px;
}
body>center:nth-last-child(3) tbody tr td:nth-child(2){
    text-align:center;
}
A.bottom_link:visited{
    color:#000;
}
A.bottom_link:link{
    color:#000;
}
A.bottom_link{
    color:#000;
}
A.bottom_link:hover{
    color:#000;
    text-decoration:none;
    box-shadow: 0px 8px 15px #a2a2a2;
}
.bottom_link{
    background:#50d6c2;
    padding:10px;
    display:inline-block;
    border-radius:7px;
    margin-top:40px;
    color:#000;
}
caption.mail{
    color:#50d6c2;
}
textarea{
    resize: none;
    width:82%;
    padding:0 1%;
}
form table tbody tr:nth-child(5) td input{
    height:35px;
    width:60px;
    font-size:15px;
    border:0;
    margin-top:20px;
    margin-right:30px;
    background:#ccc;
    border-radius:5px;
}
form table tbody tr:nth-child(5) td input:nth-child(1){
    background:#50d6c2;
}
form table tbody tr:nth-child(5) td input:hover{
   box-shadow: 0px 8px 15px #a2a2a2;
}
.warn{
    color:#50d6c2;
    font-weight:bold;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/mail_send_result.php") {
A.bottom_link{
    margin-left:20px;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/mail_show.php") {
body > center:nth-last-child(6) {
    box-shadow: 0px 8px 15px #a2a2a2;
    border: none;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/userinfo.php") {
center>h3{
    color:#fff;
    padding-top:30px;
}
}
