css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("beyondthefarhorizon.com") {
th{
   background-image:none !important;
   color:#042 !important;}
td.row1 {
   background-color:#003333 !important;}
td.row2 {
   background-color:#009966 !important;}
td.row3 {
   background-color:#096!important;}
td.rowpic {
   background-image:none!important;}
.postdetails {
   color:#042!important;
   font-size:11px!important;}
a.topictitle:link {
   color:#042!important;}
a:link, a:active, a:visited {
   color:#066!important;}
.gen, .genmed, .gensmall { 
   color:#065!important;}
a.gen, a.genmed, a.gensmall {
   color:#042!important;}
a.gen:visited, a.genmed:visited, a.gensmall:visited {
   color:#086!important;}
a.gen:hover, a.genmed:hover, a.gensmall:hover {
   color:#0CC!important;
   background-color:#000!important;}
 
body {
   background-color:#042!important;}
hr {
   border-color:#006666!important;}
.bodyline {
   border:1px solid #00AAB1!important;}
.forumline {
   border:2px solid #009966!important;}
th {
   background-image:none !important;
   color:#006699 !important;}
td.cat, td.catHead, td.catSides, td.catLeft, td.catRight, td.catBottom {
   background-color:#066!important;
   background-image:none!important;}
td.row3Right, td.spaceRow {
    background-color:#006666!important;}
.maintitle {
   color:#00FFFF!important;}
.mainmenu {
   color:#00FFFF!important;}
a.mainmenu {
   color:#00FFCC!important;}
a.mainmenu:hover {
   color:#0000CC!important;}
.cattitle {
   color:#00FFCC!important;}
a.cattitle {
   color:#00FFCC!important;}
.forumlink {
   color:#00FFCC!important;}
a.forumlink {
   color:#00FFCC;}
a.forumlink:hover {
   color:#0C0!important;}
.nav {
   color:#00FFFF!important;}
a.nav {
   color:#00FFCC;}
.topictitle, h1, h2 {
   color:#00FFFF!important;}
a.topictitle:link {
   color:#00FFCC!important;}
a.topictitle:visited {
   color:#006699!important;}
a.topictitle:hover {
   color:#00C!important;}
.name {
   color:#00FFFF!important;}
.postdetails {
   color:#00FFFF!important;}
a.postlink:link {
   color:#00FFCC!important;}
a.postlink:visited {
color:#006699!important;}
.code {
   border:1px solid #000!important;
   color:#00FFFF!important;}
.quote {
   border:1px solid #000!important;
   color:#00CCCC!important;
   font-family:Century,serif!important;}
.copyright {
   color:#0CC!important;
   font-family:Georgia,serif!important;}
a.copyright {
   color:#0CC!important;}
a.copyright:hover {
   color:#00F!important;}
input, textarea, select {
   border-color:#000!important;
   color:#00FFFF!important;
   font-family: Georgia,serif!important;}
input.post, textarea.post, select {
   color:#00FFFF!important;} 
input.button {
   background-color:#003333!important;
   color:#00FFFF!important;
   font-family:Georgia,serif!important;}
.helpline {
   background-color:#006666!important;}
th.thHead, th.thSides, th.thTop, th.thLeft, th.thRight, th.thBottom, th.thCornerL, th.thCornerR{
   background-color:#086!important;
   color:#000!important}
.postbody{
   color:#069!important;}}
