}
div[style="width:100%;height:768px;position:fixed;left:0;top:95px;overflow:hidden;visibility:hidden;z-index:1000000000000000000"], .adtrue-div, #arc-widget-container{
    display:none;
}
div[style="position: absolute; top: 95px; left: 0px; z-index: 2147483647; width: 100%; height: 768px; overflow: hidden; visibility: hidden;"], div[style="position: absolute; top: 95px; right: 0px; z-index: 2147483647; width: 100%; height: 768px; overflow: hidden; visibility: hidden;"], div[style="padding:20px 0"], .u1a85eo4{
    display: none !important ;
    border-radius: 100px;
    clip-path: circle(0);
}
}

@-moz-document url-prefix(https://disqus.com/embed/comments/) {

.post-content .post-message::after, .post-content .post-message::before {
    color:#fff !important ;
}
.publisher-anchor-color a{
    color:#b698f7 !important
}
a[data-action="upvote"], .count {
    color:#71f999 !important
}
.vote-down, span[data-role="dislikes"]  {
    color:#f97171 !important
}

.comment__header .author{
    color: rgb(126, 187, 240) !important;
}
.icon-facebook::before {
    color:#fff !important;
}
.icon-facebook {
    display: inline-block;
    background: #4961B0;
    width: 18px !important;
    height: 18px !important;
    border-radius: 3px;
}
.icon-twitter::before {
    color:#fff !important;
}
.icon-twitter {
    display: inline-block;
    background: #2DA6ED;
    width: 18px !important;
    height: 18px !important;
    border-radius: 3px;
}
.icon-link::before {
    color:#fff !important;
}
.icon-link {
    display: inline-block;
    background: #edda2d;
    width: 18px !important;
    height: 18px !important;
    border-radius: 3px;
}
}

@-moz-document url-prefix(https://embedsito.com/) {

iframe[data-glx="yeap"],
.fontget-button, 
.ipprtcnt{
