css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.twitternotifier-status {
padding:2px;
width:260px;
margin-bottom:1px;
background:#FFFFFF !important;
color:#333333 !important;
font-family:Arial,Tahoma,sans-serif !important;
font-size:13px !important;
border-bottom: 1px dashed #BBBBBB !important;
-moz-border-radius:2px !important
}


.twitternotifier-status[unread="true"]{
background:#EEEEEE !important;
color:#000000 !important;
}

.twitternotifier-status:hover{
background:#F7F7F7 !important
}

.twitternotifier-status-user {
  color: #000 !important;
}

.twitternotifier-scrollbox { 
  border: 3px solid #fff !important;
  background: #fff !important;
}

.twitternotifier-popup {
-moz-binding:url("chrome://twitternotifier/content/twitternotifier.xml#twitternotifier-popup");
background:#F7F7F7 !important;
color:#000000 !important;
border:1px solid #666666 !important;
}

.twitternotifier-status-info, .twitternotifier-status-info a { 
  color: #AAAAAA !important;
  font-family:Georgia,serif !important;
  font-style:italic !important;
  font-size: 11px !important;
}

.twitternotifier-status-info a:hover { 
  color: #777777 !important;
}
