css:
@-moz-document url-prefix('https://www.torrentbd'), url('https://www.torrentbd.net') {
body {
    font-family:'lato', sans-serif;
    --main-bg: #242529;
    /* The main background-color */
    --border-color: #bccdff00;
    --nav-bg: #242529;
    /*Navigation bar background*/
    --body-bg: #212225;
    font-weight: 400;
    --btn-1-color: #084646;
}
.switch label input[type="checkbox"]:checked + .lever {
    /* Theme switching lever */
    background-color: #293f3d;
}
.torr-card .content-title h6 {
    /* Title bar of a torrent when you view it */
    font-size: 22px;
    font-weight: 500;
}
.row .col.l8 {
    /*details under a torrent*/
    width: 70%;
    font-size: .8em;
    font-weight: 600;
}
.row .col.l4 {
    /*same as above*/
    width: 30%;
    margin-left: 0;
}
.material-icons {
    color: #158a8e;
    /* Icons such as the like buttons, the icons under your profile (settings, notifications)*/
}
.card-image {
    padding-top: 15px
}
.preview-trigger {
    background-color: #084646
}
.forum-page-title {
    background: #242529 !important;
}
.content-title h6 {
    font-size: 1.3rem;
    padding-bottom: 2px;
}
.card .card-action {
    /*hides border over material icons*/
    border-top: none;
    padding: 20px;
}
.nav-wrapper, .content-title, .posted-on {
    background: #242529 !important;
}
.green.darken-1, .blue.darken-2 { /*Go to top background color */
    background-color: var(--btn-1-color) !important
}
a#kuddus-trigger.btn-large {
    /* Search Icon background color */
    background-color: #2A2B33;
}
#kuddus-trigger i {
    /* Search Icon color */
    text-shadow: none;
    font-size: 2em;
    color: #ccc;
}
#kuddus-trigger {
    border: 4px solid #9e9e9e00;
}
#kuddus-trigger-container {
    top: 3px;
}
.switch-label {
    background-color: #2d544e
}
.card {
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.15);
    border-radius: 5px;
}
.z-depth-1, nav, .card-panel, .card, .toast, .btn, .btn-large, .btn-floating, .dropdown-content, .collapsible, .side-nav {
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
}
.row {
    border-radius:5px;
}
.card-content, #shouts-container, #torrents-main {
    /* Details written on the left pane under your dp */
    font-weight: 400;
    font-family:'nunito', sans-serif;
    color: #ddd;
    font-size: 20px;
}
.collapsible-header, .collapsible {
    border-color:transparent
}
.tbdrank {
    font-weight: 400;
}
.topsl-btn {
    box-shadow: none;
    height: 39px;
    border: 2px solid #0c4675 !important;
}
.cnav {
    padding-bottom: .4%;
}
.post-body {
    font-size: 1.1em
}
.comment-header {
    background: transparent;
}
.comment-row {
    border: none;
    box-shadow: 1px 5px 8px rgba(0, 0, 0, 0.1)
}
.blue-grey.darken-2 {
    background-color: #21222590 !important;
}
.data-val, .torrents-table a, .popular-torrents a, .online-users-content a, .collection a.collection-item, .trending-lists a, .pagination li, .pagination li a, .side-nav a {
    font-family: 'lato';
}
}
