css:
@-moz-document url-prefix('https://www.torrentbd'), url('https://www.torrentbd.net') {
body {
    font-family:'lato', sans-serif;
    --main-bg: #eee;
    /* The main background-color */
    --border-color: #bccdff00;
    --nav-bg: #2a4251;
    /*Navigation bar background*/
    --body-bg: #eee;
    font-weight: 400;
    --btn-1-color: #084646;
    color: #333 !important
}
.material-icons {
    color: #4391a8;
    /* Icons such as the like buttons, the icons under your profile (settings, notifications)*/
}
.nav-wrapper, .content-title, .posted-on {
    /* Torrent Title */
    background: #eee !important;
    color: #3C4F62;
}
.posted-on {
    color: #333 !important
}
.card-panel {
    background:#eee
}
.collapsible-header {
    background: #eee
}
.forum-page-title {
    background: #eee !important;
    color: black;
}
.switch label input[type="checkbox"]:checked + .lever {
    /* Theme switching lever */
    background-color: #293f3d;
}
.torr-card .content-title h6 {
    /* Title bar of a torrent when you view it */
    font-size: 22px;
    font-weight: 500;
}
hr {
    opacity: 0.05
}
.row .col.l8 {
    /*details under a torrent*/
    width: 70%;
    font-size: .8em;
    font-weight: 600;
}
.row .col.l4 {
    /*same as above*/
    width: 30%;
    margin-left: 0;
}
.card-image {
    padding-top: 15px
}
.preview-trigger {
    background-color: red
}
header {
    box-shadow: 0px 0px 15px -6px rgba(0, 0, 0, 0.1);
}
.content-title h6 {
    font-size: 1.3rem;
    padding-bottom: 2px;
}
.card .card-action {
    /*hides border over material icons*/
    border-top: none;
    padding: 20px;
}
.card {
    margin-top: 0;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    border-radius: 5px;
    background: #eee
}
.z-depth-1, nav, .card-panel, .card, .toast, .btn, .btn-large, .btn-floating, .dropdown-content, .collapsible, .side-nav {
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
}
.row {
    border-radius:5px;
}
.green.darken-1 {
    /*Go to top background color */
    background-color: #344B62 !important
}
a#kuddus-trigger.btn-large {
    /* Search Icon background color */
    background-color: #3b5164;
}
#kuddus-trigger {
    border: 4px solid #9e9e9e00;
}
#kuddus-trigger i {
    /* Search Icon color */
    text-shadow: none;
    font-size: 2em;
    color: #ccc;
}
#kuddus-trigger-container {
    top: 3px;
}
.switch-label {
    background-color: #2d544e
}
.card-content, #shouts-container, #torrents-main {
    /* Details written on the left pane under your dp */
    font-weight: 400;
    font-family:'nunito', sans-serif;
    font-size: 20px;
}
.collapsible-header, .collapsible {
    border-color:transparent
}
.tbdrank {
    font-weight: 400;
}
.topsl-btn {
    box-shadow: none;
    height: 39px;
    /*border: 2px solid #0c4675 !important;*/
}
.cnav {
    padding-bottom: .4%;
}
.post-body {
    font-size: 1.1em
}
.comment-header {
    background: transparent;
}
.comment-row {
    border: none;
    box-shadow: 1px 5px 8px rgba(0, 0, 0, 0.07)
}
.crumb-container, .crumb-container a {
    color: #233e42;
    font-weight: 400;
    font-size: 1rem;
}
table.bordered > thead > tr, table.bordered > tbody > tr {
    border-color: transparent
}
.likebtn {
    color: #333 !important;
}
.data-val, .torrents-table a, .popular-torrents a, .online-users-content a, .collection a.collection-item, .trending-lists a, .pagination li, .pagination li a, .side-nav a {
    color: RGB(45,65,74);
    font-family: 'lato';
}
.forums-panel-items-1 a {
    color: RGB(45, 65, 74) !important;
}
}
