css:
@-moz-document url-prefix("https://www.nicovideo.jp/user/"), url-prefix("https://www.nicovideo.jp/my") {
.HeaderContainer-ad {
    display: none;
}
.UserPageFooterContainer-ad {
    display: none;
}


.UserPageHeader {
    padding-bottom: 0;
}
.UserPageHeader-inner {
    width: 100%;
}
.HeaderContainer-main {
    display: flex;
    height: auto;
}
.UserDetailsHeader {
    width: 100%;
    padding: 0 5% 20px 5%;
}

.UserDetailsHeader-body {
    margin-top: 0;
    display: flex;
    flex-wrap: wrap;
    width: 100%;
}
.UserDetailsHeader-bodyHeader {
    margin-bottom: 0;
}
.UserDetailsStatus {
    margin: 0;
}
.UserDetailsStatusItem-body {
    margin: 0;
}

.UserDetailsStatus {
    margin: 15px 0px 15px 10px;
    width: auto;
}
.UserDetailsStatus-item:first-child {
    padding: 0;
}
.ExpandBoxButton_expanded {
    margin: auto;
    position: static;
}
/*左*/
.UserDetailsHeader-left {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
}
.UserDetailsHeader-icon {
    width: 50px;
    height: 50px;
}
.UserDetailsHeader-level {
    margin: 0;
    padding: 0 5px;
    width: 50px;
}
.UserDetailsHeader-action {
    width: 100%;
    margin: 0;
}

/*フィードバックを大きく*/
.FeedbackLink {
    width: 250px;
    height: 0;
    padding: 30px;
    font-size: 20px;
    color: #333;
    border: #999 solid 1px;
}
.FeedbackLink:before {
    width: 35px;
    height: 35px;
}
}
