css:
@-moz-document url-prefix("https://www.nicovideo.jp/user/"), url-prefix("https://www.nicovideo.jp/my") {
body {
    background: #e8e8e8;
}

.MylistMediaObject-bodyTitle {
    font-size: 20px;
    font-weight: bold;
    color: #000;
}
.MylistMediaObject-bodyDescription {
    margin: 4%;
    color: #333;
}
.VideoMediaObject-bodyTitle{
    font-size:16px;
    font-weight:bold;
}


/*フッター*/
footer {
    background: #cacaca;
    padding: 32px 64px;
    margin: 0;
}

/*フィードバックを大きく*/
.FeedbackLink {
    width: 250px;
    height: 0;
    padding: 30px;
    font-size: 20px;
    color: #333;
    border: #999 solid 1px;
}
.FeedbackLink:before {
    width: 35px;
    height: 35px;
}
}
