css:
@-moz-document url-prefix("https://www.nicovideo.jp/user/"), url-prefix("https://www.nicovideo.jp/my") {
.RightSideContainer {
    display: none;
}
.HeaderContainer-ad {
    display: none;
}

body {
    min-width: 100%;
}
.UserDetailsHeader {
    width: 100%;
    padding: 0 5% 20px 5%;
}
.UserPageHeader-inner {
    width: 100%;
}

.UserDetailsHeader-body {
    margin-top: 0;
}
.UserDetailsStatus {
    margin: 15px 0;
}
.MainMenuContainer-menuList {
    width: 100%;
    padding: 0;
}
.UserPage-main {
    width: 100%;
    padding: 0% 10%;
}

/*フッター*/
.UserPageFooterContainer-ad {
    display: none;
}
.UserPageFooterContainer-links {
    padding: 30px;
}

.UserPageFooterContainer {
    margin: 0;
    padding: 0% 10%;
    width: 100%
}
.FooterContainer {
    margin: 0;
    padding: 0% 10%;
    width: 100%;
}

/*フィードバックを大きく*/
.FeedbackLink {
    width: 250px;
    height: 0;
    padding: 30px;
    font-size: 20px;
    color: #333;
    border: #999 solid 1px;
}
.FeedbackLink:before {
    width: 35px;
    height: 35px;
}
}
