css:
@-moz-document domain("channel4.com") {
/* Adverts */
#c4ad-Middle1, #c4ad-Right1, #c4ad-Right2, #c4ad-Right3, 
.promo-slot, .ad, #c4ad-Top, #c4ad-BottomRight, #c4ad-BottomLeft, 
.backgroundAdvert, .leaderboardAdvert, .leaderboardAdvert--adjustedForBackgroundAd, .leaderboardAdvert--loaded,
.advertsSponsor, .all4-promo-banner, .advertsLeaderboard, .advert-empty
{display:none!important}

/* Backgrounds */
body {background: #1e1e1e !important}
.page {background-color: #000 !important}
.crossSelling-slices {background: #111 !important}

/* Navbar */
.all4gn {background: #000 !important}
.all4gn a {color: #DDD !important}
.all4gn .all4gn__home-image {filter: brightness(3) !important}
.all4gn .all4gn-footer {background: #000 !important}

/* Episode/Series/Promo Colours */
.episodeGuide {background: #111 !important}
.episodeGuide-episodeTitle, .episodeGuide-seriesTitle, .brandPromo-item-title, .corporate-title, .theme-more4 .paletteLink {color: #FFF !important}
.episodeGuide-episodeSummary, .episodeGuide-seriesSummary, .brandPromo-item-secondaryTitle, .corporate-secondaryTitle {color: #888 !important}
.brandPromo-item, .brandPromo-item-content, .ourPicks-item-content {background: #1e1e1e !important}
.episodeGuide-availabilityFilter {background: #444d57 !important}

/* Icon/Image Dark Adaptations */
span.tooltip img {filter: brightness(2) !important}
.imageBlock--4x3:before, .imageBlock--16x9:before {background: #1e1e1e !important}
.ourPicks-item-content:before {filter: invert(1) !important}
.all4gn .all4gn-footer-corporation-disclaimer {filter: brightness(200) !important}

}
