css:
/* Menu */
h2.sidebar-header.bg-secondary {
    float: left;
    padding-left: 20px;
    margin-bottom: -5px;
}

h2.sidebar-header.bg-secondary {
    display: none;
}


/* Character Profiles */
div.auth.route-character div.container.py-2.py-md-3 .page,
div.auth.route-character\.name div.container.py-2.py-md-3 .page {
    display: contents;
}

div.auth.route-character div.container.py-2.py-md-3,
div.auth.route-character\.name div.container.py-2.py-md-3 {
    max-width: unset;
}

/* Character Name */
div.bg-secondary.p-2.card-header.big-content.mb-0 > div > div > div > h2 {
    font-size: 28px;
}

div.auth.route-character .sidebar,
div.auth.route-character\.name .sidebar {
    margin-bottom: 0px;
    border-bottom: 1px solid #0e0d0c;
    width: 100% !important;
    max-width: 100%;
    display: inline-block !important;
    position: relative;
}

div.auth.route-character .sidebar .nav-link,
div.auth.route-character\.name .sidebar .nav-link {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    font-size: 14px;
    float: left;
}

div.auth.route-character .sidebar a.nav-link,
div.auth.route-character\.name .sidebar a.nav-link {
    display: inline-block !important;
}


div.auth.route-character .top-buttons,
div.auth.route-character\.name .top-buttons {
    position: absolute;
    right: 25px;
    top: 207px;
}

div.auth.route-character .top-buttons .btn,
div.auth.route-character\.name .top-buttons .btn {
    background-color: transparent !important;
    box-shadow: none !important;
    margin-left: 0px !important;
    border-radius: 0 !important;
}

div.auth.route-character .top-buttons .btn.bookmarked,
div.auth.route-character\.name .top-buttons .btn.bookmarked {
    background-color: #0072bc !important;
    box-shadow: none !important;
}

div.auth.route-character .top-buttons .btn.bookmarked:hover,
div.auth.route-character\.name .top-buttons .btn.bookmarked:hover {
    background-color: red !important;
    box-shadow: none !important;
}

div.auth.route-character .top-buttons .btn:hover,
div.auth.route-character\.name .top-buttons .btn:hover {
    background-color: #0072bc !important;
    box-shadow: none !important;
}

.sidebar .nav-link:not(.bg-primary):not(.active):hover {
    color: #f7f7f6;
}

.sidebar .nav-link:not(.active):hover,
.top-buttons .btn:hover {
    background: #0072bc;
}

/* Baseball Card */
/* Sub Badge */
#tab0 > div.bg-secondary.p-2.card-header.big-content.mb-0 > div.row {
    display: contents;
}

#tab0 > div.bg-secondary.p-2.card-header.big-content.mb-0 > div {
    display: block !important;
}

.bg-secondary.p-2.card-header.big-content.mb-0 {
    font-size: 13px;
}

.bg-primary.p-1.col-xl-2.col-lg-3.col-md-4.col-6.text-truncate.mt-2.rounded {
    position: absolute;
    left: 190px;
    top: 300px;
    background: #242320 !important;
    width: 130px;
}

.big-content .character-avatar {
    height: 100px;
    width: 100px;
    border-top-left-radius: 0px;
    margin: 0rem .5rem .5rem 0rem !important;
    float: left;
}

#tab0,
.card-footer .row {
    display: contents;
}

#tab0 > .bg-light.p-2 {
    float: right;
    width: calc(100% - 300px);
}

#tab0 > .card-header {
    float: left;
    display: inline-table;
    width: 300px
}

#tab0 > div.bg-secondary.p-2.card-header.big-content.mb-0 > div > div > div.row > div {
    width: 100% !important;
    max-width: 100% !important;
    flex: none;
    padding-right: 10px;
    text-align: right;
}

#tab0 > div.bg-secondary.p-2.card-header.big-content.mb-0 > div > div > div.row {
    clear: both;
}

.infotag-label {
    float: left;
}


/* Footer Area */
.card-footer {
    clear: left;
}

.card-footer > div.row > div {
    text-align: left;
    margin-left: 0px;
}

.card-footer span.quick-info-label,
.card-footer span.quick-info-value {
    font-size: 12px;
}

.card-footer span.quick-info-label {
    color: #a4a29f;
    font-weight: bold;
}

.card-footer span.quick-info-value {
    float: right;
    margin-right: 8px;
}

.character-kinks-block {
    clear: both;
}

/* Common Tab declarations */
div.character-images  > .row,
#character-friends > .row {
    text-align: center;
}

div.character-images a,
#character-friends > .row > .card {
    margin: auto;
}

/* Images Tab*/
div.character-images  > .row {
    margin-left: 0px;
    background: #242320;
    margin-right: 0px;
    margin-top: 0px;
}

div.character-images a {
    background-color: #393835 !important;
    border: 1px solid #393835 !important;
    margin-top:5px;
    max-width: 150px;
    padding: 10px 0px;
    overflow: hidden;
}

div.character-images a:hover {
    background-color: #393835 !important;
    border: 1px solid #393835 !important;
    margin-top:5px;
    max-width: 150px;
    padding: 10px 0px;
    overflow: hidden;
}

div.character-images img:hover {
    transform: scale(1.5);
}

/* Guestbook */
div.guestbook img.character-avatar {
    box-shadow: none;
}

div.guestbook span.guestbook-message {
    display: block;
    background: #393835;
    margin-left: 150px;
    width: calc(100% - 295px);
    margin-right: 145px !important;
    padding: 10px;
    margin-top: -100px;
    border-radius: 10px;
    clear: both;
    float: left;
}

div.guestbook div.p-2.my-2.rounded.bg-white {
    background-color: #242320 !important;
}

div.guestbook div.comment {
    background-color: #242320 !important;
}

div.comment > div > a > img {
    float: right !important;
}

div.guestbook div.comment > div > div {
    margin-left: 130px;
    background: #393835;
    padding: 10px;
    border-radius: 10px;
    width: calc(100% - 260px);
}

div.guestbook > .card.big-content.bg-light {
    border-bottom: none;
}

div.guestbook div.small {
    float: left;
    width: 120px;
    flex-grow: unset !important;
    position: absolute;
    top: 103px;
    text-align: center;
}

div.comment span.small {
    float: right;
    width: 100px;
    display: block;
    position: absolute;
    top: 103px;
    right: 0;
}

/* Friends */
#character-friends > .row {
    margin-left: 0px;
    background: #242320;
    margin-right: 0px;
    margin-top: 0px;
}

#character-friends > .row > .card {
    background-color: #393835 !important;
    margin-top: 5px;
    padding-bottom: 10px;
    max-width: 150px;
    min-width: 150px;
}

/* Info */
div.route-character .card > .card-body > div.mb-n2 > .col-md-6.col-lg-4.col-xl-3.mb-2,
div.auth.route-character\.name .card > .card-body > div.mb-n2 > .col-md-6.col-lg-4.col-xl-3.mb-2  {
    text-align: right;
    padding-right: 30px;
    width: 400px !important;
    padding-left: 50px;
}

/* RP Ads - personals */
div.route-personals div.row div.small > span {
    float: right;
    width: calc(100% - 120px);
    text-align: right;
    padding-right: 10px;
}

/* Groups */
.route-groups .card span.bbcode,
.route-groups\.browse .card span.bbcode {
    margin-left: 150px;
    display: block;
}

