css:
@-moz-document url("https://news.ycombinator.com/") {

    body { background-color: #000;   margin: 0;}

    center>table:first-child {  background-color: #000; }
    
    .title { font-size: 20pt; color:#3DC100; }
    
    .comhead { font-size: 14pt;  margin-left: 10pt; }
    
    .subtext { font-size: 10pt; padding-bottom: 10pt; }
    
    .score { font-weight: bold;  color: #AEAEAE; }
    
    .votearrow { width:inherit; height:inherit; border-left:10px solid transparent; border-right:10px solid     transparent; border-bottom:15px solid #AEAEAE; background: none; margin :10px; }
    
    .itemlist { margin: 20px; }
    
    a:link { color:#3DC100; }
    
    a:visited { color: #4D790D; }
    
    .pagetop a:link { color:#000; }
    
    .pagetop a:visited { color:#000; }

}
