css:
@-moz-document domain("alienwarearena.com")
{

body, .news__listing, .giveaways__listing, .videos__listing {
  background-color: #111;
}

.featured-widget.news, .featured-widget.videos, .featured-widget.giveaway {
  background-color: black;
}

.common__landing-comments__post-meta {
  background-color: #333;
}

.giveaways__listing-post__title, .giveaways__top-widget__box, .giveaways__top-widget__description, .giveaways__top-widget__instructions, .giveaways__top-widget__description, .news__listing-post__title, .row *.col-12.col-md-4 a, .videos__listing-post__title, footer.footer, .forums__btn-sideways, .side-panel, .forums__btn-sideways.right, .giveaways__top-widget__description h1, .giveaways__top-widget__description em, .giveaways__top-widget__description strong, .panel, .panel-widget, .discussion__op, .forums__topics, .news__landing, .ucf-form__wrapper, .card-body, .videos__landing, .giveaways__top-widget__description li, .giveaways__top-widget__description u {
  color: white !important;
  background-color: #222 !important;
}

.subtitle, .title, .news__top-widgets__box-content, .news__top-widgets__box-header h1, .news__top-widgets__box-blurb, .news__top-widgets__box-action, .videos__top-widget__box-header h1, .videos__top-widget__box-blurb, .videos__top-widget__box-meta, .videos__top-widget__box-action, label, .forums__btn-sideways__label, .panel-widget__table a, .time-ago, .panel-widget__heading, p, .hidden-xs, .btn, .btn i, .forums__topic-link, .table-row.header, .news__landing-header , .news__landing-header h1, .news__landing  span, .tile-slider__card .card-body a, .videos__landing-header h1, .videos__landing-content, .profile-username span, .score-total, a:not(.user-voted).up-vote i, a:not(.user-voted).down-vote i, .pull-left, .pull-left span, #main div.forums-list-widget__title > a, .forums__topic-link, .text-title, #sidebar ul li.active>a, strong {
  color: white !important;
}

.giveaways__top, body, .slider-cell, .news__top, .videos__top {
  background-image: none !important;
}

a:not(.btn):before, .more-link.right:before {
  border-color: white !important;
}

span[data-notify="message"] p, .form-control, .profile-subtitle, .common__landing-comments__post-content pre, .text-muted i, .page-link i {
  color: #999 !important;
}

.container-fluid a:not(.forums__topic-link) {
  color: #00baf8;
}

.news__landing *, .news__landing-content ul li, a:not(.btn) {
  color: #999 ;
}

.btn-primary {
  border: 2px solid #999;
}

div#content-report-form label, .modal-footer button {
  color: #000 !important;
}

}
