css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.kooora.com") {
  
  body > table > tbody > tr > td + td + td.iconmenu, td.plain, td.header > table > tbody > tr + tr + tr > td.empty,
  object#FLASH_AD {
    display: none !important;
    width: 0 !important;
    max-width: 0 !important;
  }
  
  body > table > tbody > tr > td + td + td.iconmenu > center {
    width: 0 !important;
    max-width: 0 !important;
  }
  
  body, body > table {
    margin-left: auto !important;
    margin-right: auto !important;
  }
  
  body > table {
    width: 100% !important;
    max-width: 960px !important;
  }
  
  td.header, td.header > table {
    width: 240px !important;
    max-width: 240px !important;
  }
  
  td.content, td.footnote {
    width: 720px !important;
    max-width: 720px !important;
  }
  
  td.content > table {
    width: 720px !important;
    max-width: 720px !important;
  }
  
  body, TD.navLang, INPUT.pickerSelect, TD.tzselector, TD.empty, TD.blank, TD.plain, TD.plainLeft, 
  TD.introbox, TD.rightBar, TD.content, TD.contentborder, TD.zi, TD.voteheader, TD.homepageicon2 {
    background-color: #F0F8FF !important;
  }
  
  TD.zh, TD.zhl {
    border-color: #F0F8FF !important;
  }

  TD.mainTitle, TD.navMenusHeader, TD.navHeader, TD.navTime, TD.regMenus1, TD.pickerTitle,
  INPUT.pickerClear, TD.topbar, TD.group_title, TD.team_info, TD.region_header0, TD.region_header1,
  TD.region_header2, TD.region_header3, TD.zh, TD.vote2header, TD.vote2graph, TD.title, TD.v, TD.d,
  TD.d2, TD.newsDate, SELECT.Edits, SELECT.datepicker, INPUT.datepicker, SELECT.seasonpicker {
    background-color: #3388CC !important;
  }
  
  TD.regMenus, TD.regMenusBottom, TD.regMenusH, TD.voteitem, TD.voteheader, TD.vote2item,
  TD.vote2graph, TD.menu, TD.menuSelected, TD.menuExtra, TD.menuSmall, TD.menuSmallSelected,
  TD.teaminfobox, TD.articlebox, TD.articlebox_more, TD.homepageicon, TD.homepageicon2,
  TD.homepagebox, TD.leagueinfobox {
    border-color: #3388CC !important;
  }
  
  TD.menuExtra, A.mnews2, A.mnews2:visited, A.tnews:hover, A.tnews2, A.tnews2:visited, 
  A.tnews2:hover, A.year, A.year:visited, SELECT.TvList1, INPUT.TvList1, SELECT.TvList2,
  INPUT.TvList2, OPTION.item {
    color: #3388CC !important;
  }
  
  TD.regMenusLogo {
    background-color: #427384 !important;
  }
  
  A.menu2, A.menu2:visited {
    color: #334466 !important;
  }
  
  TD.mainTitle3, TD.s_p, TD.picker1, INPUT.pickerOK, TD.menuSelected, A:hover, A.menu2:hover,
  A.newslink:hover, A.mnews:hover, A.mnews2:hover, A.tnews, A.tnews:visited, A.year:hover, 
  A.yearA:hover, A.yearR:hover, A.yearT:hover, INPUT.Edits, OPTION.header {
    color: #FE7010 !important;
  }
  
  TD.marqueetitle {
    background-color: #FE7010 !important;
  }
  
  TD.votefooter, TD.votefooter0 {
    color: #555555 !important;
  }
}
