css:
@-moz-document domain("zerohedge.com")
{
	@media print {
		.header-container{
			display: none !important;
		}
		main, #main {
			display: block !important;
		}
		main .sidebar-left-wrapper, main .sidebar-right-wrapper{
			display: none !important;
		}
		main .layout-content{
			display: block !important;
		}
		.newsletter-insert{
			display: none !important;
		}
		p{
			page-break-inside: avoid !important;
			break-inside: avoid !important;
		}
		.node.node--view-mode-full img{
			width: 100% !important;
			max-width: inherit !important;
		}
		.picture, .img{
			page-break-inside: avoid !important;
			break-inside: avoid !important;
			display: block !important;
			width: 100% !important;
			max-width: inherit !important;
		}
		.ad__wrapper-element, #block-contributorblock {
			display: none !important;
		}
		aside.layout-featured{
			display: none !important;
		}
		#block-zerohedge-page-title{
			display: block !important;
		}
		footer.node__meta{
			display: block !important;
		}
		.a2a_kit {
			display: none !important;
		}
		a[href*='//']:after, a:visited[href*='//']:after {
			content:" (" attr(href) ") ";
			color: #aaa !important;
			text-decoration: none !important;
		}
		.qc-cmp-persistent-link{
			display: none !important;
		}
	}
}
