css:
@-moz-document domain("www.youtube.com") {
 :root {
   --border-small: 5px !important;
   --border-medium: 7px !important;
   --border-big: 10px !important;
   --border-button: 50px !important;
 }
 div.html5-video-container,
 video.video-stream.html5-main-video {
   border-radius: var(--border-big);
 }

 div.ytp-chrome-bottom {
   border-radius: var(--border-small);
 }

 div#container.style-scope.ytd-searchbox {
   border-top-left-radius: var(--border-small);
   border-bottom-left-radius: var(--border-small);
 }

 button#search-icon-legacy {
   border-top-right-radius: var(--border-small);
   border-bottom-right-radius: var(--border-small);
 }

 yt-img-shadow img.style-scope.yt-img-shadow {
   border-radius: var(--border-medium);
 }

 ytd-subscribe-button-renderer paper-button,
 div#sponsor-button ytd-button-renderer paper-button {
   border-radius: var(--border-button);
 }

 ytd-thumbnail-overlay-resume-playback-renderer.style-scope.ytd-thumbnail,
 div#progress {
   border-bottom-left-radius: var(--border-medium);
   border-bottom-right-radius: var(--border-medium);
 }

 ytd-guide-entry-renderer[active] {
   border-radius: var(--border-small);
 }

 #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover,
 #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:focus {
   border-radius: var(--border-small);
 }

 ytd-secondary-search-container-renderer#secondary > div#contents ytd-watch-card-rich-header-renderer {
   border-top-right-radius: var(--border-medium);
   border-top-left-radius: var(--border-medium);
 }

 div#container.style-scope.ytd-playlist-panel-renderer,
 div.header.style-scope.ytd-playlist-panel-renderer {
   border-radius: var(--border-medium);
 }

 ytd-playlist-panel-video-renderer[selected] {
   border-radius: 3px;
 }

 ytd-playlist-panel-video-renderer:hover {
   border-radius: var(--border-small);
 }
}
