css:
@-moz-document url-prefix("https://campusvirtual.ug.edu.ec/") {
.card-body a {
    color: #5bc0de;
}

.card-body {
    background-color: #373a3c;
    color: white;
}
.navbar-bootswatch {
    background-color: #373a3c;
}
.navbar-light .navbar-brand, .navbar-bootswatch .navbar-brand {
    color: white;
}
.usertext{
color:#5bc0de
}
.navbar-light .navbar-nav .nav-link:hover, .navbar-bootswatch .navbar-nav .nav-link:hover, .navbar-light .navbar-nav .nav-link:focus, .navbar-bootswatch .navbar-nav .nav-link:focus ,nav-link{
    color: #5bc0de;
}
.navbar-light .navbar-nav .nav-link, .navbar-bootswatch .navbar-nav .nav-link {
    color: #5bc0de;
}
#page-my-index {
    background-color: #211f1f;
    color: white;
}
.card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
    position: relative;
    display: flex;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #373a3c;
    -webkit-background-clip: border-box;
    background-clip: border-box;
    border: 1px solid #373a3c;
    border-radius: .25rem;
}
.bg-white {
    background-color: #373a3c!important;
}
.calendar_event_course {
    background-color: white;
}
.list-group-item {
    position: relative;
    display: block;
    padding: .75rem 1.25rem;
    margin-bottom: -1px;
    background-color: #373a3c;
    border: 1px solid rgba(0,0,0,.125);
}
.btn-secondary, .btn-default, .btn:hover {
    color: #5bc0de;
    background-color: #373a3c;
    border-color: #5bc0de;
}
.pagination {
background-color:#373a3c!important;
}
#page {
    margin-top: 50px;
    background-color: #211f1f;
}
#region-main {
    overflow-x: auto;
    overflow-y: visible;
    border: 1px solid rgba(0,0,0,.125);
    border-radius: .25rem;
    padding: 1.25rem;
    background-color: #fff;
}
}
