css:
@-moz-document url-prefix("https://www.reddit.com/r/goodanimemes/") {



#header {
    background: url("https://i.imgur.com/YR42LMR.png");
    height: 36vh;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
}
.choice {
    color:#5C98FF !important;
    background:none !important;
}
.md {
	color: #FF5A4E;
}

h1 {
    color:black !important;
}

.side .titlebox .flairtoggle, .side .sidebox .spacer {
    
    background-color:transparent;
}
.subscribers .number::after {
    color:#5C98FF !important;
}

.side .subscribers .number {
    
     color:#5C98FF !important;
}

.side .users-online .number {
     color:#5C98FF !important;
}

.linkinfo {
    background:#fff;
}
.linkinfo {
	padding: 5px;
	/* border: 1px solid #5f99cf; */
	font-family: arial,helvetica,sans-serif;
	font-size: larger;
	border-radius: 3px;
}

.linkinfo > div {
    color:#1D1B1D;
}

.md > p {
    color:#1D1B1D;
}

.md > textarea {
    
   background:#fff; 
    
}
.md > textarea {
	color: #1D1B1D !important;
}

.sitetable.nestedlisting .thing.noncollapsed.comment {
	 background: #FFF !important; 
}

.comment .score.unvoted {
	color: #FF5A4E;
}
.comment .score.likes {
	color: red;
    font-size: larger;
    font-weight:600;
}


.nextprev a:hover, .next-suggestions a:hover {
	background: #FF5A4E;
	border: 1px solid #FF5A4E;
}

.nextprev a, .next-suggestions a {
	padding: 1px 4px;
	background: #000;
	border: 1px solid #000;
	border-radius: 3px;
	font-weight: bold;
}

.side {
	border: 1px solid #CCCCCC;
}

.pagename > a {
	color: #fff !important;
    font-size:3em;
    text-shadow: #000 6px 1px, #000 -1px 1px, #000 -1px -1px, #000 1px -1px;
}

#header-img.default-header {
	text-indent: -9999px;
	background-image: url("https://i.imgur.com/7o4LdHA.png");
	background-position: 0px 0px;
	background-repeat: no-repeat;
	height: 150px;
	width: 120px;
	display: inline-block;
	vertical-align: bottom;
	margin-bottom: 0px;
    margin-left:20px;
}


.link .usertext-body .md {
    
    background-color:#FFF;
}

.users-online .number::after {
    color:#5C98FF;
}



#header .tabmenu li.selected a {
    background-color:#5C98FF !important;
    color: #fff !important;
}

#header .tabmenu {
    padding:0;
box-shadow:0px !important;
}

#header .tabmenu li {
box-shadow:none !important;
}


#header-bottom-right {
	position: absolute;
	right: 0px;
	bottom: 0px;
	/*background-color: #fff;*/
	padding-right: 2.6vw;
    padding-left:2.6vw;
    padding-top:1.3vh;
    padding-bottom:1.3vh;
	line-height: 12px;
	border-top-left-radius: 0px;
}


a.give-gold.login-required.access-required.gold-give-gold {
	color: #DAA520;
}


#sr-header-area {
	background-color: #fff;
	white-space: nowrap;
	text-transform: uppercase;
	border-bottom: 0px solid gray;
	font-size: 90%;
	height: 18px;
    padding:2px;
	line-height: 18px;
}

#header .tabmenu li a {
    border:0px !important;
    border-radius:0px !important;
    border-bottom:0px !important;
    position:relative;
    display:inline-block;
    font-family: Verdana, Geneva, sans-serif;
    background-color:rgba(60, 60, 60, 0.9) !important;
    color:white !important;
    height:3vh;
    padding-top:0.9vh;
    padding-left:1vw;
    padding-right:1.9vw;
    width:6vw;
    font-size:12px;
    font-weight:600;
    box-shadow:0px !important;
    border-width: 0 0 0px 0px !important;
    margin-bottom:1px;

}


.tagline .moderator, .green, .search-result-meta .moderator {
    
    color: #2eb82e;
}

blockquote {
 background:#e6e6e6!important
}


#header .tabmenu li a::after {
	right: -7px;
	border-bottom-left-radius: 6px;
	border-width: 0 0 0px 0px !important;
	box-shadow: -0px 0px 0 #4F4F4F;
}

#header .tabmenu li a::before, #header .tabmenu li a::after {
	position: absolute;
	margin-bottom: 0px;
	width: 0px !important;
	height: 0px !important;
	content: " ";
	border: 0px solid #6F6F6F;
}

.modal-body > form.thing {
    
    background:#fff;
}


.md > :last-child, .md .-cells > :last-child, .md .-lists > :last-child, .md .-blocks > :last-child, .md .-headers > :last-child, .md h1 > :last-child, .md h2 > :last-child, .md h3 > :last-child, .md h4 > :last-child, .md h5 > :last-child, .md h6 > :last-child, .md th > :last-child, .md td > :last-child, .md ul > :last-child, .md ol > :last-child, .md .-lists > :last-child, .md pre > :last-child, .md blockquote > :last-child, .md table > :last-child, .md p > :last-child, .md ul > :last-child, .md ol > :last-child {
	margin-bottom: 0;
	padding: 5px;
}

.thing {
	background: #FFF;
	border-bottom: 1px solid #CCCCCC !important;
	padding-left: 20px;
}

#header-bottom-left {
    position:absolute;
    bottom:0;
    width: 100%;
}
.link .score {
    text-align:center;
    font-family: Verdana, Geneva, sans-serif;
    font-size:1em;
    color:#FF624E;
}



#header {
	border-bottom: 0px solid #5f99cf;
	position: relative;
	background-color: #cee3f8;
	z-index: 99;
}
.thing.stickied.link a.title {
    font-weight:bold;
    color:#47AE32;
}
.link .rank {
    float:left;
    margin-top:15px;
    color:#c6c6c6;
    font-family:Verdana;
    font-size:1.4em;
    text-align:right margin-left 10px;
}
body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, form, fieldset, input, p, blockquote, th, td, iframe {
    margin:0;
    padding:0px;
}
.thing {
    background:#FFF;
    border-bottom:1px solid #CCCCCC;
    padding-left:20px;
    padding-top:10px;
}
.entry a.title {
    color:#5C98FF;
    font-family: Verdana, Geneva, sans-serif;
    font-size:1.15em;
    letter-spacing:0.7px;
}
.entry .buttons li a {
    color:#FF5A4E;
    font-weight:bold;
    padding:0 1px
}
.side {
    background-color:#FFF;
    padding-top:25px;
    padding-right:50px;
    padding-left:50px;
}
.side .redditname a {
    color:#FF5A4E;
}
.titlebox h1 {
    font-family:arial, verdana, helvetica, sans-serif;
    margin:0px;
    margin-bottom:5px;
    font-weight:bold;
    font-size:40px;
}
.side .submit-link a {
    background-color:#EA5A4E;
    color:#FFF;
    border-radius: 6px;
    padding: 5px;
}
.side .md p {
    color:#1D1B1D;
    font-family: Verdana, Geneva, sans-serif;
    font-size:1.07em;
}
.side .submit-link a {
    background-color:#EA5A4E;
    color:#FFF;
    border-radius: 6px;
    padding: 5px;
}
#search input {
    background-color:#FFF;
}
res-selected #sr-header-area, .listing-page, .side, .comments-page, .spacer, .form.toggle.flairtoggle, .submit-page {
    background:#FFF;}


