css:
@-moz-document url-prefix("https://glimesh.tv/") {
body {
    background: #fff;
    color: #000;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #000;
}
p {
    color: #111;
}
.text-muted {
    color: #444 !important;
}
hr {
    border-color: #304aca;
}
.navbar,
.bg-dark {
    background: #f2f2f2 !important
}
.navbar-dark .navbar-nav .nav-link {
    color: #000;
}
.navbar-dark .navbar-nav .nav-link:hover {
    color: #304aca;
}
.img-avatar {
    border-color: #999;
}
.widget {
    background: #f2f2f2;
}
.widget h5 {
    color: #000;
}
.badge-danger {
    background: #e01f2c;
}
.chat-system .chat-box {
    background-image: url('https://cdn.discordapp.com/attachments/546788355396861969/745769407946424460/unknown.png')
}
.chat-system .chat-box .bubble.you,
.chat-system .chat-box .bubble.you:before {
    background-color: #fff;
    color: #111;
}
.text-white {
    color: #000 !important;
    ;
}
.chat-system .chat-box .chat-footer.chat-active {
    background: #f2f2f2;
}
.chat-system .chat-box .chat-input input {
    background: #fff;
    border: 1px solid #cccccc;
    color: #000;
}
.chat-system .chat-box .chat-input svg {
    color: #304aca;
    fill: #fff;
}
.navbar-dark .navbar-nav .nav-link:focus,
.navbar-dark .navbar-nav .nav-link:hover {
    color: #304aca;
}
.dropdown:not(.custom-dropdown-icon) .dropdown-menu {
    background: #fff;
    filter: drop-shadow(0px 0px 6px rgba(50, 50, 150, 0.9));
}
.dropdown:not(.custom-dropdown-icon) .dropdown-menu a.dropdown-item:hover {
    background: #e6e6e6;
}
a.text-warning,
a.text-warning:focus,
a.text-warning:hover {
    color: #304aca !important
}
.card {
    background: #f2f2f2;
    border: none;
}
.form-control {
    background: #fbfbfb;
    border-color: #ccc;
    color: #111;
}
.form-control:active,
.form-control:focus {
    background: #fff;
    border-color: #304aca;
    color: #000;
}
.input-group .input-group-prepend .input-group-text {
    background: #304aca;
    color: #fff;
    border: #304aca;
}
.form-group label,
label {
    color: #222;
}
.form-control:disabled,
.form-control[readonly] {
    background-color: #d9d9d9 !important;
    color: #444;
}
.coming-soon-cont .coming-soon-content > h4 {
    color: #000;
}
.coming-soon-cont .coming-soon-content > p:not(.terms-conditions) {
    color: #222;
}
.coming-soon-cont .coming-soon-content > h3 {
    color: #304aca
}
#large-header {
    filter: brightness(299%) !important;
}
.coming-soon-image img {
    filter: brightness(40%) drop-shadow(1px 1px 4px #000) !important;
}
.coming-soon-cont .coming-soon-wrap form .field-wrapper button.btn {
    position: absolute;
    top: 72px;
    border-radius: 5px;
    padding: 10px 30px;
}
.badge {
    padding: 3px 7px;
}
.chat-system .chat-box .conversation-start span {
    background: #e6e6e6;
    color: #000;
    border: 1px solid #304aca;
}
.coming-soon-container input[type=checkbox],
.coming-soon-container input[type=radio] {
    margin: 5px !important;
}
.social-icon {
    border: none;
}
.social-icon i {
    color: #595959;
}
.simple--counter-container .s-counter {
    color: #333
}
/* 
*   Light Mode v2 Uppdate
*/
::-webkit-resizer,
::-webkit-scrollbar-corner {
    border-bottom-right-radius: 5px;
    background-color: #e1e1e1;
}
::-webkit-scrollbar-button,
::-webkit-scrollbar-track {
    background: #e1e1e1;
}
::-webkit-scrollbar-thumb {
    background-color: #bfbfbf !Important;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #b3b3b3 !Important;
}
::-webkit-scrollbar-thumb:active {
    background-color: #1b55e2 !Important;
}
::-webkit-scrollbar-track-piece {
    background-color: #e1e1e1;
}
.list-group-item {
    background: #f2f2f2;
    color: #333;
    border-color: #ccc;
}
.list-group-item.active,
.list-group-item:focus,
.list-group-item:hover {
    background: #1b55e2;
    color: white;
    border-color: #1b55e2 !important;
}
.list-group-item:focus {
    background: #1b55e2;
    color: white;
    border-color: #1b55e2 !important;
}

.card-stream-username {
    color: #000;
}
.card-img-overlay .card-title {
    color: white;
}
.card-stream .card-stream-streamer {
    background: rgba(250, 250, 250, .8)
}
/* v3 */
.table > thead > tr > th,
.user-content-body table > thead > tr > th {
    color: black;
}
.table,
.user-content-body table {
    background: #e4e4e4;
}
.table > tbody > tr > td,
.user-content-body table > tbody > tr > td {
    color: #111;
}
.table td,
.table th,
.table thead th,
.user-content-body table td,
.user-content-body table th,
.user-content-body table thead th {
    border-color: #f2f2f2!Important;
}
.dropdown-item {
    color: #2a2a2a !important
}
}
