css:
@-moz-document domain("jeja.pl") {
.images-box video {
    position: inherit;
    z-index: 99999;
}
body {
    background: #000;
}
a.hand-up-down span {
    color: orange !important;
}


.movie-top {
    -webkit-box-shadow: 0 28px 11px 0 rgba(0, 0, 0, .83);
    -moz-box-shadow: 0 28px 11px 0 rgba(0, 0, 0, .83);
    box-shadow: 0 28px 11px 0 rgba(0, 0, 0, .83);
}
.dow-right-text-shadow {
    box-shadow: inset 0 -190px 115px -84px rgb(45, 45, 45);
    -moz-box-shadow: inset 0 -190px 115px -84px rgb(45, 45, 45);
    -webkit-box-shadow: inset 0 -190px 115px -84px rgb(45, 45, 45);
}

.images-arrow-right {
    padding-left: 20%;
}
.images-arrow-left {
    padding-right: 20%;
}

.komunikat-18 {
    background: #222;
}

p,
span,
strong,
h4 a,
h3,
h2,
h1,
textarea,
label,
select,
option,
label::before,
.module-c li,
.footer-5 strong,
.profil-liczby-right div,
.list-badges li,
.box-1x1-text,
.text,
.profil-opis-text,
.profil-dane-right,
.nick,
.page-text,
.left-wrap,
.ico-kom span,
.ico-ulu span,
.ico-f span,
.search::before,
.active-login,
.left-wrap div[style*="color: #212121;"],
.dow-left-text li {
    color: #999 !important;
}



footer::before,
#jeja_ifr,
#wrapper-wrap,
.movie-top,
.list-chat li.form::before {
    background: #111 !important;
}

textarea,
tags,
select,
input,
#search p,
#search h2,
#nav a,
#search,
#root,
#content,
.list-c li,
.sort a,
.notifications li,
.rozwin-click strong,
.zwin-click strong,
.menu-right li,
.list-c::before,
.menu,
.box-autor,
.movie-s-box,
.game-s-box,
.pow-user:hover,
.select-down,
.best-month-box,
.module-b,
.game-su-box,
.occasional-s-box {
    background-color: #222 !important;
}

.input::before {
    color: #555 !important;
}

.box-autor-name {
    color: #666;
    font-weight: bold;
}

.list-d li,
.sort .active a,
.movie-active,
#nav .active a {
    background-color: #444 !important;
}

.cText p a[href^="https://www.jeja.pl/user,"],
a.comment-login {
    font-weight: bold;
}

.movie-active:hover {
    background-color: #555 !important;
}

/* Ramki */
.info-1,
li.success {
    background-color: #051d06 !important;
}

.info-1 p,
li.success {
    color: #6dba46 !important;
}

.info-2,
li.error {
    background-color: #220202 !important;
}

.info-2 p,
li.error {
    color: #ba4646 !important;
}

.info-3,
li.info {
    background-color: #0c2443 !important;
}

.info-3 p,
li.info {
    color: #33afad !important;
}

/* Z jakiegoś powodu usuwanie konta na mobilnej nie ma klasy ehhhh */
ul.notification li {
    background-color: #0c2443;
    color: #33afad;
}

.info-4,
li.warning {
    background-color: #43390f !important;
}

.info-4 p,
li.warning {
    color: #bead50 !important;
}
/* */
#footer-wrapper {
    background: #222 !important;
}
.double:before,
.list-comment:before {
    background: #444 !important;
}

.num .orangeColor.bolder {
    color: white !important;
}

.rej-text-roz,
.rej-text-roz a,
.com-pop-up,
.list-a > li:before {
    background: #333;
}

.log-box-mes p {
    background-color: #777;
    color: #000 !important;
}


.req {
    color: #b4b4b4;
}

#top,
#top:before,
h1#logo,
#top .menu {
    background: #111 !important;
}

.log-box-select-down,
.lob-box-select:hover,
.komentarze-add,
.zglos-add,
.active:not(.ico-ulu),
.active.checkbox,
.menu-right a:hover,
.tagCloud a,
.slide-box:hover,
#nav,
h1,
h2,
h3,
.form-edit-comment,
.hover {
    background-color: #222 !important;
}


.list-chat li {
    background-color: #666;
}

.log-box-select-arrow-on,
.background-change,
#search h2 {
    display: none;
}

a:not(.hand-up-down):not(.disabled-link),
.pow-user-nag,
input {
    color: #888 !important;
}

.pagination-number.active-page {
    background-color: #555 !important;
}


/* Przyciski */
.button-exp-users,
.buttonBlue,
.dys-add,
.sendReport,
.play,
.add,
.komunikat-18-button,
.add-game,
.add-pocz,
.next-button,
.pagination-number,
.pagination-no-numer,
.grupy-right-submit,
.edit-comment-save,
.edit-comment-cancel,
.button-formlike,
.sendComment,
.list-e a,
.list-d.a.slider:before,
.komentarze-submit,
.pagination-b a,
button,
li.next a,
li.prev a,
#fullscreen_button,
.list-chat li.a,
.button-page {
    background-color: #333 !important;
    color: #777 !important;
}

.status {
    color: #669365 !important;
}

.titleThumb {
    color: #999 !important;
    text-shadow: 1px 1px 10px #000;
}

.menu.has-notifications::before,
.menu::before {
    color: #666;
}

.tagify {
    --tag-bg: #333;
    --tag-hover: #444;
}

.meu-right a,
.list-d h3,
.game-su-box h3,
form h3,
.komentarze-show-top h3,
.icon-msg,
.icon-eye,
.game-s-box h3,
.temat-box h3,
.module-b h2,
.module-b h3,
.list-c h3,
.grupa-box h3,
.dow-left-text h3,
.delete-my-comment,
.notification a,
.voteup.active,
.votedown.active,
.checkbox.active #nav h2 a,
.list-a h3 {
    background-color: rgb(0, 0, 0, 0) !important;
}

* {
    border-color: #444 !important;
}

.komentarz-odp-all,
.komentarz-odp-all-hide {
    color: #9e6b00;
}


/* Poprawione ikonki ktore normalnie nie maja przezroczystosci */
.tematy-ikon,
.komentarze-add-icon,
.zglos-add-icon {
    width: 1px;
    height: 1px;
    padding: 7px;
}

.komentarz-down-button .block-user:not(:hover) {
    background: url('https://v4.jeja.pl/images/komentarz_icon_8_on.png') no-repeat left top;
}

.komentarz-down-button .delete-comment:not(:hover) {
    background: url('https://v4.jeja.pl/images/komentarz_icon_7_on.png') no-repeat left top;
}

.quote-text {
    background: url('https://i.imgur.com/1Eug9AQ.png') no-repeat left top !important;
}

img[src="https://v4.jeja.pl/images/tematy_icon_1.png"] {
    background: url('https://i.imgur.com/8yLXuqN.png') !important;
}

img[src="https://v4.jeja.pl/images/tematy_icon_2.png"] {
    background: url('https://i.imgur.com/Lbg0MUA.png') !important;
}

.icon-msg {
    background: url('https://i.imgur.com/LVnjG1Q.png') no-repeat left top;
}
.icon-msg:hover {
    background: url('https://i.imgur.com/B2pth9l.png') no-repeat left top;
}
.icon-eye,
.edit-date-show,
.game-fb {
    background: url('https://i.imgur.com/NZUS8L2.png') no-repeat left top;
}
.icon-eye:hover {
    background: url('https://i.imgur.com/NDJSh0R.png') no-repeat left top;
}
.komentarze-add-icon,
.zglos-add-icon {
    background: url('https://i.imgur.com/eAtFeCe.png') !important;
}
.grupa-box-new,
.temat-box-lista-bottom-new,
.edit-date-show2,
.temat-box-new {
    width: 0px;
    height: 0px;
    padding: 9.5px;
    background: url('https://v4.jeja.pl/images/box_new.png');
}

.edit-date-show {
    width: 0px;
    padding: 6px 9px;
}

.message-icon {
    width: 0px;
    padding: 6px;
    background-image: url('https://v4.jeja.pl/images/status_icon_on.png');
}

nav#nav h2 {
    background: #666 !important;
}



/* Logo w wersji dla ciemnego tła (Źródło: https://www.jeja.pl/info,99,odswiezone-logo-jejapl.html) */
.logo,
#logo a {
    padding: 21.5px 71.5px 21.5px 71.5px;
    background-image: url('https://i.imgur.com/5seM0y7.png');
    background-size: 143px 43px;
    width: 0px;
    height: 0px;
}

/** Przyciemnianie img i elementów, którym nie da się ustawić koloru **/
.odz-boks,
.logo,
.list-badges,
.list-g .badge img,
.exp-smile,
.icon-m-box {
    filter: brightness(0.8);
}

.box-ster-row {
    filter: invert();
}

.hand-up-down {
    filter: grayscale(0.4);
}

.icon-home,
.pagination-arrow-left,
.pagination-arrow-right,
.komentarze-ref-icon {
    filter: grayscale() brightness(2);
}
.delete-comment:not(:hover),
.block-user:not(:hover) {
    filter: grayscale();
}

.star-box .voteup:not(.active),
.star-box .votedown:not(.active) {
    filter: brightness(2);
}
.star-box .voteup:hover,
.star-box .votedown:hover {
    filter: brightness(1) grayscale(0.2);
}
.star-box .voteup.active,
.star-box .votedown.active {
    filter: brightness(0.9);
}
}
