css:
@-moz-document domain("open.spotify.com") {
html {
    background: #e7e6e6;
/*     scrollbar-color: rgba(0,0,0,.2) transparent; */
}

body {background: white;}

.Root__nav-bar, ._6fe74cef24d5ae2ba99a619cab07aef4-scss {
    background-color:#fff;
}

/* hide logo */
[aria-label=Main] [role=banner] {display: none;}
.Rootlist + div {display: none;}


/* selected side-item */
.de74c1f13a9a8150dfe21bf59c967111-scss {
    background-color: #dcdcdc;
}

/* sidebar devider */
.Rootlist__divider {
    background-color: #ddd;
}

/* sidebar button */
.Root__nav-bar button {color: #000 !important;}

/* topbar nav button */
[aria-label="Top bar and user menu"] button[title="Go back"], [aria-label="Top bar and user menu"] button[title="Go forward"] {
    background-color: rgba(0, 0, 0, .36);
}

/* topbar after scroll */
.Root__top-bar header[aria-label="Top bar and user menu"] > div:first-child  {background-color: #fff0 !important;}


.Root__top-bar header[aria-label="Top bar and user menu"] > div:first-child > div {
    background-color: #ffffffad;
    backdrop-filter: blur(10px);
}


.contentSpacing > div:first-child > div:first-child {
    background: #cbcbcb75;
    box-shadow: 0 1px 3 0 #00000021;
    backdrop-filter: blur(10px);
}

.contentSpacing > div:first-child > div:first-child {       color: black;
}

/* topbar menu link & button */
.Root__top-bar button {background-color: #00000012; color:black;}
.Root__top-bar li>button {background-color: white;}
.Root__top-bar button:hover, .Root__top-bar a:hover {background-color: #0000001f;}


/* cards ðŸš¥*/
._3802c04052af0bb5d03956299250789e-scss {
    background: #fff0;
    padding: 20px 10px 16px;
}

._2218f5ab2ec765b2e30ce4c9e256ee35-scss, .cca57225423267f53f4e944a3373f89a-scss
{ box-shadow: 8px 10px 30px 0 rgba(0, 0, 0, .2), 0px 1px 2px 0 rgba(0, 0, 0, .1);}


/* hover on cards */
._3802c04052af0bb5d03956299250789e-scss:hover {background-color: #0000000a;}
section.contentSpacing > div > div:hover {background-color: #0000000a;}


/* exeptions */
._3802c04052af0bb5d03956299250789e-scss.bf37551e6974dfc6b7bc2307c4bc8d52-scss {background: linear-gradient(149.46deg,#450af5,#8e8ee5 99.16%) !important; padding: 20px 20px 16px;}


/* card - play btn */
.a576b3947647edea47972737accee656-scss {
    box-shadow: 0px 2px 2px 0px #0000000d !important;
}


/* card tite */
.main-view-container__scroll-node-child .contentSpacing .react-contextmenu-wrapper a {color: #414141 !important;}

/* card - artist subtitle */
.main-view-container__scroll-node-child  .e80fc2e59729be32410c909c47ef87a3-scss {
    font-size: 13px ;
    margin-top: -3px;
}


/* ========== now-playing-bar =========== */
.now-playing-bar {
    background: linear-gradient(0deg, rgba(255, 255, 255, .8) 10%, rgba(255,255,255, .5) 50%, rgba(255,255,255,0) 100%);
    backdrop-filter: blur(10px);
}

.Root__now-playing-bar, footer.now-playing-bar-container {background: none !important; border-top:none; }

.Root__main-view {height: 110%; padding-bottom: 40px;}


/* text search headers */
.contentSpacing h2 {color: black !important;}


/* all texts */
.tracklist-row .play-pause, .track-name, .position, .tracklist-row .tracklist-col.name .track-name-wrapper .artists-album a, .total-duration, .btn, a, body, .recently-played .navlist .navlist-item .navlist-itemlink .type, .inputBox-input {
    color: #0000006e !important;
    text-shadow: 0 0 0 #333 !important;
}

/* sidebar items */
a.link-subtle[aria-current="page"] {background-color: white !important;}

a.RootlistItem__link { color: #3e3e3e !important ;}

/* text banners */
._6a740396901e931bea871079e4cdfa58-scss h3 {
    color:white;
}

/* topbar menu */
._7d5bb5bfd500c0c322fb865b69181717-scss, nav[role=menu] {
    background-color: #fefefe;
    border: 1px solid #0000001c;
    box-shadow: 0 2px 2px rgba(0,0,0,.1);
}
/* top menu devider */ .b1b6f67740779440315549e39354d155-scss {border-top: 1px solid #ededed;}

/* inclusive menu */
.react-contextmenu-item.active, .react-contextmenu-item:hover {
    color: black;
    background-color: #2bdf8b;
    border-color: #33333317;}



/* sidebar items */
.RootlistItem__link:hover {color:#2bdf8b !important;}

/* search rectangle large cards */
[aria-label=Songs] .react-contextmenu-wrapper > div:hover {background-color: #0000000f;}

/* =========== artist page =============== */

[data-testid=topbar-content] a[aria-current=page], .contentSpacing a[aria-current=page] {background-color: #0000001c !important;}

.main-view-container__scroll-node-child p span {color:#6262623d;}

/* action-bar buttons */
[data-testid="action-background-container"] button:not([title=More]):not([aria-label=Play]):not([title="Save to Your Library"]),
.b49b68b14a1771a4cb36313f2b350e84-scss._2b37b3630aee3cbfc97689b5af341d60-scss,
.content [data-testid="action-bar-row"] button:not([title=More]):not([aria-label=Play]):not([title="Save to Your Library"]) {
    font-size: 13px;
    color: #000;
    background-color: #ffffff00;
    border: 1px solid black;
}

.b49b68b14a1771a4cb36313f2b350e84-scss._2b37b3630aee3cbfc97689b5af341d60-scss:hover,
.content [data-testid="action-bar-row"] button:not([aria-label=Play]):not([title="Save to Your Library"]):hover {background-color: #00000017; cursor:pointer ;}

.verifiedBadge + span + span {color:#000;}

/* =========== album page =============== */

/* top bar */
[data-testid=topbar-content-wrapper] span {color:hsl(0deg 0% 43% / 70%);}

/* hero */
.main-view-container__scroll-node-child > section > div > div:nth-child(2)
{background: linear-gradient(#ffffff00,rgba(255, 255, 255, .5));}

.main-view-container__scroll-node-child section.content [data-testid="container"] {background: linear-gradient(#ffffff00,rgba(255, 255, 255, .5));}


._59ed5f1313c7c4b211995d2b6463683f-scss
,[data-testid="action-background-container"] {background-image: linear-gradient(rgba(255, 255, 255, .5),#ffffff);}

.main-view-container__scroll-node-child section > div {overflow: visible;}

._8891c8f9b257b02a0bc56b136f5a5fbc-scss span {color: hsla(0, 0%, 34%, .29);}


/* podcast-list header */
._571922307568e54e63ba83058433369d-scss._2e77de28f0b30f1b6e8d479009f45e0e-scss,
[data-testid=action-background-container] h2 {color: #000 !important;}


/* album list */
.tracklist-row .tracklist-duration {color: black}
.tracklist-col.more Button, .spoticon-ellipsis-16, [data-testid=action-bar-row] .spoticon-heart-32, .spoticon-ellipsis-32 , .second-line-separator {color: black !important;}
.track-row__episode-date {color: black;}

.tracklist-row:focus .tracklist-play-pause, .tracklist-row:hover .tracklist-play-pause {color: #2bdf8b}


.tracklist-row:hover {
    background-color: #0000000a !important;
    outline: 1px solid #0000000a !important;
}

.track-name-wrapper .progress-bar .progress-bar__bgÂ {background-color: hsla(0, 0%, 0%, 0.08);}
._4fceee214dd1a7cf6306183221534425-scss {background-color: hsla(0, 0%, 0%, 0.09);}

/* Bottomlist Button */
.PlaylistRecommendedTracks__refresh button, .tracklist-col.tracklist-col-recommended-add button {color: black;}



/* 404 */
.spoticon-album-64 , .spoticon-album-64 + p , .spoticon-album-64 + p + p ,
.spoticon-artist-64 , .spoticon-artist-64  + p , .spoticon-artist-64  + p + p {color: black;}







nav.navBar {
    background: #eee !important;
}

.artists-album-separator {
    color: #ccc !important;
}
.tracklist-row {
    transition: 0s !important;

}
.progress-bar__fg {
    background-color: #2bdf8b !important;
}
.control-button--active, .tracklist-row.active .duration, .tracklist-row.active .play-pause, .tracklist-row.active .position, .tracklist-row.active .track-name {
    color: #1BAE50 !important;
}
.navBar .group {
    border-top: 1px solid #000000 !important;
}
.ads-container {
    background-color: transparent !important;
    border-top: 1px solid transparent !important;
}
.inputBox-input {
    border-bottom: 1px solid #ccc !important;
}
.btn.btn-black {
    background: transparent !important;
    border: 2px solid #333 !important;
}
.btn.btn-green {
    background-color: #2bdf8b !important;
    color: #fff !important;
}
.connect-bar {
    background-color: #2bdf8b !important;
}
.connect-bar:after {
    border-bottom: 10px solid #2bdf8b !important;
}
.control-button:hover {
    color: #000 !important;
}
.control-button, .tracklist-play-pause {
    color: #333;
}
.control-button--circled:after, .navBar-group {
    border-color: #333 !important;
}
.control-button--active-dot:after {
    background-color: #2bdf8b !important;
}
.tracklist-row--active .position, .tracklist-row--active .tracklist-duration, .tracklist-row--active .tracklist-name, .tracklist-row--active .tracklist-play-pause {
    color: #2bdf8b !important;
}
.now-playing-bar-container {
    border-color: #ddd !important;
}
.tracklist-name {
    color: #000 !important;
}
section h1, .mo-info-name, .navBar-group-header {
    color: #333 !important;
}
.navBar-item .type, .inputBox-label {
    color: #888 !important;
}
input {
    caret-color: #333 !important;
}
}

@-moz-document domain("open.spotify.com") {
.Rootlist__playlists-header
, .Rootlist__playlists-header + div
, .Rootlist__playlists-header + div + div
{
    display: none;
}

.Rootlist {margin-top: 0px}
}
