/* ==UserStyle==
@name           Decluttered Outlook
@version        1.0.11
@description    Removes all advertisement banners, favorites, groups und the header of folders.
@author         floriegl
@license        CC0
@namespace https://greasyfork.org/users/703184
==/UserStyle== */
 
@-moz-document url-prefix("https://outlook.live.com/mail/") {
/* Top ad banner */
div#app > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(2) > div > div > div > div:nth-child(3) > div > div > div > div:nth-child(4) > div > div > div > div > div > div> div > div:nth-child(1) > div > div:not([role]) {
    display: none !important;
}
 
/* "Upgrade" banner */
div#app > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(2) > div > div > div > div:nth-child(1) > div > div > div:nth-child(2) {
    display: none !important;
}
  
/* Favorites in sidebar */
div#app > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(2) > div > div > div > div:nth-child(1) > div > div > div:nth-child(1) > div > div:nth-child(2) {
    display: none !important;
}
 
/* Folder header in sidebar */
div#app > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(2) > div > div > div > div:nth-child(1) > div > div > div:nth-child(1) > div > div:nth-child(3) > div:nth-child(1) {
    display: none !important;
}
 
/* Groups in sidebar */
div#app > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(2) > div > div > div > div:nth-child(1) > div > div > div:nth-child(1) > div > div:nth-child(5){
    display: none !important;
}
}
