css:
@-moz-document domain("gigglehd.com") {
body, .bd textarea, .bd .itx:hover, .bd .itx:focus, .bd textarea:hover, .bd textarea:focus{
    background-color : #111;  /* 백그라운드 검정색 */
    color : white;
}

div, .xe_content, .bd_lst td.title a , A:link, .fdb_itm .meta a, .widgetZineA li .title, .widgetTableA td .board, .widgetTableA td .category, .bd em, .bd .color, .bd_zine h3{
	color : white;
}

.bd_tb_lst td, .bd_tb_lst .hx:visited, A:visited, #top_mn .tg_btn, #top_mn .login_btn, #top_mn .sitemap, .rd_hd .board .date, .mhcomm_normal .pic .date{
	color : #aaa; /* 커뮤니티 게시글 제목 (방문한) */
}

.mhcomm_normal .pic .name, .widgetA .author, .widgetTableA td.title a, .widgetZineA li p.text{
	color : #ccc; /* 위젯 작성자 */
}

.rd_hd .board .top_area, .cmt_editor {
	background : #222 !important;
}

span, strong, .bd .cTab .on>a{
	color : white !important;
}

.bd_tb_lst th, .bd .cTab>li>a{
	background-color : #222;
    font-weight: 900;
}

.bd_btn, .btn_img{
	background : #111 !important;
    color : white;
    text-shadow : none;
    box-shadow : none;
}

#gnb_wrp, #gnb .a1{
	background : #444;
    text-shadow : none !important;
    color : white;
}

.bd_tb_lst .notice td,.bd_zine li.notice,.bd_tb_lst .select,.bd_tb_lst tbody tr:hover, .cmt_wrt_btm .fdb_tag, .bd .img_tx a, .bd_pg .this, .bd_pg a:hover, .bd_pg a:focus{
	background : #222;
}

#hd h1 .home{
	filter : invert(1);
}
.no_img{

	filter : invert(0.80);
}

.bd .attached_image{
	filter : invert(0.5);
}

.widgetTabA li, .widgetTabA li a{
	background : #222;
    color : white;
    
}


}
