css:
@-moz-document domain("afterpay.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,
.orders .orders-header,

.header-account .link.active,

.core-modal .modal-container
{
  background: #3d3d3f;
}

.table-row.table-content.striped:nth-child(even),

.amount-due,
.amount-due .period-unit-wrapper .active-period,

.account .header-account-section,
.header-account .link, .billing .header-account-section, .preferences .header-account-section, .page-security .header-page-security-section,

.personal-information-section > div:nth-child(1), .payment-methods-section > div.container, .view-section.ng-scope > div > form > section > div:nth-child(1),
.view-section.ng-scope > div > div:nth-child(2),

/*Rebrand Update*/
.orders-banner__panel,

.order-product-summary .product-item-row
{
    background: #363639;
}

header, .input, .header,
.table-row.table-header,

.table-row.table-content:hover,

.button-round,

.amount-due .period-unit-wrapper,

.pay .stored-payment-method,

/*Rebrand Update*/
.bg-white
{
    background-color: #2e2e2f !important;
}

.z-2
{
    background: #363639 !important;
}

.bg-mint
{
  background-color: #223931;
}

.separator,

/*Rebrand Update*/
.order-product-summary .product-summary
{
  background: #0000 !important;
}

body, .input, ::placeholder,
blockquote, dd, dl, figure, h1, h2, h3, h4, h5, h6, p, pre,

.table-row.table-header,

.button-round,

/*Rebrand Update*/
.rebrand .link, .rebrand .link.active,
.orders-banner__panel,

.z-2 *, .text-off-black,
.btn-pinky,

.v2_breadcrumbs .breadcrumb
{
  color: #ccc !important;
}

.rebrand .link:active, .rebrand .link:hover
{
  color: #fff !important;
}

*
{
  border: none !important;
  border-color: #0000 !important;
  box-shadow: none !important;
}

.header
{
  margin: 0 !important;
  width: 100%;
  max-width: none;
  padding: 0 3em;
  height: 83px;
}

body > div.application-wrapper > section.view-section.ng-scope > div > section > div > div > div.column-33.top.ng-scope,

/*Rebrand Update*/
.mobile-link__img
{
  display: none;
}

.order-delivery .delivery-address, .order-delivery .store-information, .order-delivery .merchant-information
{
  width: fit-content !important;
}

.main-header .logo img,
.logo img,

/*Rebrand Update*/
.svg-container>svg,
.flyer-panel__logo img,

.ns\:mr-4,

.bg-mint img
{
    filter: invert(1) hue-rotate(185deg) saturate(6) brightness(1);
    mix-blend-mode: color-dodge;
}
}
